import OpenAI from 'openai';

const openai = new OpenAI({
  apiKey: process.env.REACT_APP_OPENAI_API_KEY,
  dangerouslyAllowBrowser: true
});

const AVAILABLE_SERVICES = [
  { id: 'lambda', name: 'AWS Lambda', icon: '/aws-icons/lambda.png' },
  { id: 's3', name: 'Amazon S3', icon: '/aws-icons/s3.png' },
  { id: 'ec2', name: 'Amazon EC2', icon: '/aws-icons/ec2.png' },
  { id: 'ecs', name: 'Amazon ECS', icon: '/aws-icons/ecs.png' },
  { id: 'dynamodb', name: 'Amazon DynamoDB', icon: '/aws-icons/dynamodb.png' },
  { id: 'rds', name: 'Amazon RDS', icon: '/aws-icons/rds.png' }
];

const generateArchitecture = async (difficulty) => {
  try {
    const completion = await openai.chat.completions.create({
      model: "gpt-4-turbo-preview",
      messages: [
        { 
          role: "system", 
          content: "You are an AWS architecture expert. Generate random architectures with multiple missing services and rank possible solutions by cost-efficiency."
        },
        {
          role: "user",
          content: `Generate a random ${difficulty} AWS architecture with 3 missing services and rank the possible answers based on cost-efficiency. Use only these services: ${AVAILABLE_SERVICES.map(s => s.id).join(', ')}`
        }
      ],
      response_format: {
        type: "json_schema",
        schema: {
          type: "object",
          properties: {
            services: {
              type: "array",
              description: "List of AWS service IDs in the architecture",
              items: { type: "string" }
            },
            connections: {
              type: "array",
              description: "Connections between services",
              items: {
                type: "object",
                properties: {
                  from: { type: "string" },
                  to: { type: "string" }
                },
                required: ["from", "to"]
              }
            },
            missingServices: {
              type: "array",
              description: "Services that need to be added to the architecture",
              items: {
                type: "object",
                properties: {
                  position: { 
                    type: "string",
                    description: "Description of where this service should go"
                  },
                  options: {
                    type: "array",
                    items: {
                      type: "object",
                      properties: {
                        service: { type: "string" },
                        costEfficiency: { 
                          type: "number",
                          description: "Cost efficiency rating from 1-10"
                        },
                        explanation: { 
                          type: "string",
                          description: "Why this service is suitable and its cost implications"
                        }
                      },
                      required: ["service", "costEfficiency", "explanation"]
                    }
                  }
                },
                required: ["position", "options"]
              }
            }
          },
          required: ["services", "connections", "missingServices"]
        }
      }
    });

    const aiSuggestion = JSON.parse(completion.choices[0].message.content);

    // Map service IDs to full service objects
    const getServiceById = (id) => AVAILABLE_SERVICES.find(s => s.id === id) || AVAILABLE_SERVICES[0];

    return {
      architecture: {
        services: aiSuggestion.services.map(id => getServiceById(id)),
        connections: aiSuggestion.connections
      },
      questions: aiSuggestion.missingServices.map(ms => ({
        text: `Which service would be most cost-efficient for ${ms.position}?`,
        position: ms.position,
        missingServices: ms.options.map(opt => opt.service),
        options: ms.options.map(opt => ({
          ...getServiceById(opt.service),
          costEfficiency: opt.costEfficiency,
          explanation: opt.explanation
        }))
      }))
    };

  } catch (error) {
    console.error('AI Error:', error);
    // Fallback with multiple missing services
    const randomServices = AVAILABLE_SERVICES.sort(() => 0.5 - Math.random());
    const baseServices = randomServices.slice(0, 4);
    const missingServices = randomServices.slice(4, 7);

    return {
      architecture: {
        services: baseServices,
        connections: [
          { from: baseServices[0].id, to: baseServices[1].id },
          { from: baseServices[1].id, to: baseServices[2].id },
          { from: baseServices[2].id, to: baseServices[3].id }
        ]
      },
      questions: missingServices.map((service, index) => ({
        text: `Which service would you add for position ${index + 1}?`,
        position: `Position ${index + 1}`,
        missingServices: [service.id],
        options: randomServices.slice(0, 4).map(s => ({
          ...s,
          costEfficiency: Math.random() * 10,
          explanation: `${s.name} could be used here`
        }))
      }))
    };
  }
};

export { generateArchitecture };
