import React, { useEffect, useState } from 'react';
import ReactFlow, { 
  Background, 
  Controls, 
  MiniMap,
  useNodesState,
  useEdgesState,
  MarkerType,
  Handle
} from 'reactflow';
import 'reactflow/dist/style.css';
import { motion } from 'framer-motion';
import { getCachedServiceIcon } from '../services/IconResolver';

const AWSServiceNode = ({ data }) => (
  <motion.div
    className={`aws-service-node ${data.isMissing ? 'missing' : ''}`}
    initial={{ scale: 0, opacity: 0 }}
    animate={{ scale: 1, opacity: 1 }}
    exit={{ scale: 0, opacity: 0 }}
    whileHover={{ scale: 1.1 }}
    transition={{ type: "spring", stiffness: 300 }}
  >
    <Handle 
      type="target" 
      position="top" 
      id={`${data.id}-target`}
      className="handle"
    />
    <div className="node-content">
      {data.isMissing ? (
        <div className="missing-service-placeholder">
          <span>?</span>
        </div>
      ) : (
        <img src={data.icon} alt={data.label} className="service-icon" />
      )}
      <span className="service-label">{data.label}</span>
    </div>
    <Handle 
      type="source" 
      position="bottom" 
      id={`${data.id}-source`}
      className="handle"
    />
  </motion.div>
);

const nodeTypes = {
  awsService: AWSServiceNode
};

const createNodeId = (serviceName) => {
  if (typeof serviceName !== 'string') {
    console.error('Invalid service name:', serviceName);
    return 'invalid-service';
  }
  return serviceName.toLowerCase().replace(/\s+/g, '-');
};

const AWSArchitectureDisplay = ({ architecture, currentQuestion }) => {
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);

  // Add a ref to track if we've logged the warning
  const hasLoggedWarning = React.useRef(false);

  useEffect(() => {
    if (architecture) {
      // Create nodes for all services
      const newNodes = architecture.services.map((service, index) => ({
        id: service.id,
        type: 'default',
        data: { 
          label: (
            <div>
              <img src={service.icon} alt={service.name} style={{ width: 30, height: 30 }} />
              <div>{service.name}</div>
            </div>
          )
        },
        position: { x: index * 200, y: Math.floor(index / 3) * 200 },
        style: service.name.includes('missing') ? { background: '#ffcccc' } : {}
      }));

      // Create edges for all connections
      const newEdges = architecture.connections.map((connection, index) => {
        const fromId = connection.from.toLowerCase().replace(/[^a-z0-9]+/g, '-');
        const toId = connection.to.toLowerCase().replace(/[^a-z0-9]+/g, '-');

        // Check if there's a missing service between these nodes
        const missingService = currentQuestion?.missingServices?.find(ms => 
          ms.position.includes(fromId) && ms.position.includes(toId)
        );

        if (missingService) {
          const placeholderId = `missing-${fromId}-${toId}`;
          newNodes.push({
            id: placeholderId,
            type: 'default',
            position: { x: newNodes.length * 200, y: 200 },
            data: {
              label: (
                <div>
                  <img src="/aws-icons/missing.svg" alt="Missing Service" style={{ width: 30, height: 30 }} />
                  <div>Missing Service</div>
                </div>
              )
            },
            style: { background: '#ffcccc' }
          });

          return [
            {
              id: `${fromId}-${placeholderId}`,
              source: fromId,
              target: placeholderId,
              type: 'smoothstep',
              animated: true,
              style: { stroke: '#4FD1C5', strokeWidth: 2 },
              markerEnd: { type: MarkerType.ArrowClosed }
            },
            {
              id: `${placeholderId}-${toId}`,
              source: placeholderId,
              target: toId,
              type: 'smoothstep',
              animated: true,
              style: { stroke: '#4FD1C5', strokeWidth: 2 },
              markerEnd: { type: MarkerType.ArrowClosed }
            }
          ];
        } else {
          return {
            id: `e${index}`,
            source: fromId,
            target: toId,
            type: 'smoothstep',
          };
        }
      }).flat();

      setNodes(newNodes);
      setEdges(newEdges);
    }
  }, [architecture, currentQuestion]);

  // Early return if no data
  if (!architecture?.services) {
    return (
      <div className="architecture-visualizer-loading">
        <span>Loading architecture...</span>
      </div>
    );
  }

  return (
    <div style={{ height: '500px' }} className="architecture-visualizer">
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        nodeTypes={nodeTypes}
        fitView
        minZoom={0.5}
        maxZoom={1.5}
      >
        <Background color="#4FD1C5" gap={16} size={1} />
        <Controls />
        <MiniMap 
          nodeColor={(node) => node.data.isMissing ? '#FF5656' : '#4FD1C5'}
          maskColor="rgba(13, 25, 35, 0.7)"
        />
      </ReactFlow>
    </div>
  );
};

export default AWSArchitectureDisplay;
