// No need to import CosmosClient anymore
class CosmosDBService {
    constructor() {
        // Define the function URLs - update with your actual URLs
        this.saveScoreUrl = process.env.REACT_APP_FUNCTION_SAVE_SCORE_URL;
        this.getLeaderboardUrl = process.env.REACT_APP_FUNCTION_LEADERBOARD_URL;

        // Check if the function URLs are configured
        const functionsConfigured = this.saveScoreUrl && this.getLeaderboardUrl;

        // MODIFY THIS LINE - Remove the development mode check to force using the Azure Function
        // this.useLocalStorage = process.env.NODE_ENV === 'development' && (!this.saveScoreUrl || !this.getLeaderboardUrl);
        this.useLocalStorage = !functionsConfigured;

        if (this.useLocalStorage) {
            console.warn("Using localStorage for score storage - AZURE FUNCTIONS NOT USED");
        } else {
            console.log("‚úÖ Azure Functions API initialized successfully - Will use Azure Functions for scores");
            console.log(`‚úÖ Save Score URL: ${this.saveScoreUrl ? 'Configured ‚úì' : 'Missing ‚úó'}`);
            console.log(`‚úÖ Leaderboard URL: ${this.getLeaderboardUrl ? 'Configured ‚úì' : 'Missing ‚úó'}`);
        }
    }

    async saveGameScore(nickname, gameType, score, metadata = {}) {
        console.log(`üéÆ SCORE SAVE ATTEMPT - Player: ${nickname}, Game: ${gameType}, Score: ${score}`);

        // Use localStorage in development
        if (this.useLocalStorage) {
            console.log(`üì¶ [LocalStorage] Saving score: ${nickname}, ${gameType}, ${score}`);
            const timestamp = new Date().toISOString();
            const scoreData = {
                id: `${nickname}_${gameType}_${timestamp}`,
                nickname,
                gameType,
                score,
                timestamp,
                ...metadata
            };

            const existingScores = JSON.parse(localStorage.getItem('gameScores') || '[]');
            existingScores.push(scoreData);
            localStorage.setItem('gameScores', JSON.stringify(existingScores));

            console.log(`‚úÖ [LocalStorage] Score saved successfully!`);
            return scoreData;
        }

        // Use function API in production
        if (!this.saveScoreUrl) {
            console.log(`‚ö†Ô∏è [Mock API] Would save score: ${nickname}, ${gameType}, ${score}`);
            return { id: "mock-id", success: false, message: "Function API not configured" };
        }

        try {
            console.log(`üîÑ Calling Azure Function: ${this.saveScoreUrl.split('?')[0]}`);

            const timestamp = new Date().toISOString();
            const scoreData = {
                nickname,
                gameType,
                score,
                timestamp,
                metadata
            };

            const response = await fetch(this.saveScoreUrl, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(scoreData)
            });

            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }

            const result = await response.json();
            console.log(`‚úÖ SCORE SAVED SUCCESSFULLY via Azure Function - ID: ${result.id || 'unknown'}`);
            return result;
        } catch (error) {
            console.error(`‚ùå ERROR SAVING SCORE to Azure Function:`, error);
            return { id: "error-id", success: false, error: error.message };
        }
    }

    async getLeaderboard(gameType, limit = 10) {
        console.log(`üèÜ LEADERBOARD REQUEST - Game: ${gameType}, Limit: ${limit}`);

        // Use localStorage in development
        if (this.useLocalStorage) {
            console.log(`üì¶ [LocalStorage] Getting leaderboard for: ${gameType}`);
            const allScores = JSON.parse(localStorage.getItem('gameScores') || '[]');
            const leaderboard = allScores
                .filter(score => score.gameType === gameType)
                .sort((a, b) => b.score - a.score)
                .slice(0, limit);

            console.log(`‚úÖ [LocalStorage] Retrieved ${leaderboard.length} leaderboard entries`);
            return leaderboard;
        }

        // Use function API in production
        if (!this.getLeaderboardUrl) {
            console.log(`‚ö†Ô∏è [Mock API] Would get leaderboard for: ${gameType}`);
            return [{ nickname: "Example", score: 100, timestamp: new Date().toISOString() }];
        }

        try {
            const url = `${this.getLeaderboardUrl}?gameType=${encodeURIComponent(gameType)}&limit=${limit}`;
            console.log(`üîÑ Calling Azure Function: ${url.split('?')[0]}`);

            const response = await fetch(url);

            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }

            const data = await response.json();
            console.log(`‚úÖ LEADERBOARD RETRIEVED SUCCESSFULLY - ${data.length} entries`);
            return data;
        } catch (error) {
            console.error(`‚ùå ERROR RETRIEVING LEADERBOARD from Azure Function:`, error);
            return [];
        }
    }
}

export default new CosmosDBService();
