import OpenAI from 'openai';

const openai = new OpenAI({
  apiKey: process.env.REACT_APP_OPENAI_API_KEY,
  dangerouslyAllowBrowser: true  // WARNING: Not recommended for production
});

const generateArchitecture = async (difficulty) => {
  try {
    const completion = await openai.chat.completions.create({
      model: "gpt-4",
      messages: [
        { 
          role: "system", 
          content: "You are an AWS architecture expert creating educational scenarios."
        },
        { 
          role: "user", 
          content: `Generate an AWS architecture scenario for ${difficulty} level with:
            - List of AWS services and their connections
            - A question about missing service
            - 3 service options including the correct answer
            Format the response as a JSON object with: architecture (services, connections), question (text, missingServices), and options array.`
        }
      ],
      response_format: { type: "json_object" }
    });

    const aiResponse = JSON.parse(completion.choices[0].message.content);
    console.log('AI Response:', aiResponse);

    // Fallback to mock data if AI response doesn't match expected format
    return {
      architecture: aiResponse.architecture || {
        services: [
          { id: 'lambda', name: 'AWS Lambda', icon: '/aws-icons/lambda.png' },
          { id: 's3', name: 'Amazon S3', icon: '/aws-icons/s3.png' },
          { id: 'ec2', name: 'Amazon EC2', icon: '/aws-icons/ec2.png' }
        ],
        connections: [
          { from: 'lambda', to: 's3' },
          { from: 'ec2', to: 's3' }
        ]
      },
      question: aiResponse.question || {
        text: 'Which compute service would you use for serverless architecture?',
        missingServices: ['lambda']
      },
      options: aiResponse.options || [
        { id: 'lambda', name: 'AWS Lambda', icon: '/aws-icons/lambda.png' },
        { id: 'ec2', name: 'Amazon EC2', icon: '/aws-icons/ec2.png' },
        { id: 'ecs', name: 'Amazon ECS', icon: '/aws-icons/ecs.png' }
      ]
    };
  } catch (error) {
    console.error('OpenAI API Error:', error);
    // Fallback to mock data on error
    return {
      architecture: {
        services: [
          { id: 'lambda', name: 'AWS Lambda', icon: '/aws-icons/lambda.png' },
          { id: 's3', name: 'Amazon S3', icon: '/aws-icons/s3.png' },
          { id: 'ec2', name: 'Amazon EC2', icon: '/aws-icons/ec2.png' }
        ],
        connections: [
          { from: 'lambda', to: 's3' },
          { from: 'ec2', to: 's3' }
        ]
      },
      question: {
        text: 'Which compute service would you use for serverless architecture?',
        missingServices: ['lambda']
      },
      options: [
        { id: 'lambda', name: 'AWS Lambda', icon: '/aws-icons/lambda.png' },
        { id: 'ec2', name: 'Amazon EC2', icon: '/aws-icons/ec2.png' },
        { id: 'ecs', name: 'Amazon ECS', icon: '/aws-icons/ecs.png' }
      ]
    };
  }
};

export { generateArchitecture };
