const AWS_ICON_CATEGORIES = {
  ANALYTICS: 'Arch_Analytics',
  APP_INTEGRATION: 'Arch_App_Integration',
  ARTIFICIAL_INTELLIGENCE: 'Arch_Artificial_Intelligence',
  COMPUTE: 'Arch_Compute',
  CONTAINERS: 'Arch_Containers',
  DATABASE: 'Arch_Database',
  DEVELOPER_TOOLS: 'Arch_Developer_Tools',
  MANAGEMENT: 'Arch_Management_Governance',
  NETWORKING: 'Arch_Networking_Content_Delivery',
  SECURITY: 'Arch_Security_Identity_Compliance',
  STORAGE: 'Arch_Storage'
};

const SERVICE_MAPPINGS = {
  // Compute Services
  'AWS Lambda': `${AWS_ICON_CATEGORIES.COMPUTE}/Arch_AWS-Lambda_16.svg`,
  'Amazon EC2': `${AWS_ICON_CATEGORIES.COMPUTE}/Arch_Amazon-EC2_16.svg`,

  // Storage Services
  'Amazon S3': `${AWS_ICON_CATEGORIES.STORAGE}/Arch_Amazon-Simple-Storage-Service_16.svg`,
  'Amazon EFS': `${AWS_ICON_CATEGORIES.STORAGE}/Arch_Amazon-EFS_16.svg`,

  // Database Services
  'Amazon RDS': `${AWS_ICON_CATEGORIES.DATABASE}/Arch_Amazon-RDS_16.svg`,
  'Amazon DynamoDB': `${AWS_ICON_CATEGORIES.DATABASE}/Arch_Amazon-DynamoDB_16.svg`,

  // Networking Services
  'Amazon CloudFront': `${AWS_ICON_CATEGORIES.NETWORKING}/Arch_Amazon-CloudFront_16.svg`,
  'Amazon Route 53': `${AWS_ICON_CATEGORIES.NETWORKING}/Arch_Amazon-Route-53_16.svg`,
  'Elastic Load Balancing': `${AWS_ICON_CATEGORIES.NETWORKING}/Arch_Elastic-Load-Balancing_16.svg`,

  // Security Services
  'AWS IAM': `${AWS_ICON_CATEGORIES.SECURITY}/Arch_AWS-Identity-and-Access-Management_16.svg`,
  'AWS WAF': `${AWS_ICON_CATEGORIES.SECURITY}/Arch_AWS-WAF_16.svg`,

  // Management Services
  'Amazon CloudWatch': `${AWS_ICON_CATEGORIES.MANAGEMENT}/Arch_Amazon-CloudWatch_16.svg`,

  // Container Services
  'Amazon ECS': `${AWS_ICON_CATEGORIES.CONTAINERS}/Arch_Amazon-Elastic-Container-Service_16.svg`,
  'Amazon EKS': `${AWS_ICON_CATEGORIES.CONTAINERS}/Arch_Amazon-Elastic-Kubernetes-Service_16.svg`
};

const S3_BUCKET_URL = 'https://hackthon-backend-files-ep-2024.s3.amazonaws.com';
const ICONS_BASE_PATH = '/amazon-icons-set/Architecture-Service-Icons_06072024';

// Add logging collection
const iconResolutionLog = {
  success: [],
  failed: [],
  total: 0
};

const getServiceIcon = async (serviceName) => {
  iconResolutionLog.total++;
  const iconPath = SERVICE_MAPPINGS[serviceName];

  if (!iconPath) {
    iconResolutionLog.failed.push({
      service: serviceName,
      reason: 'No mapping found'
    });
    console.warn(`âŒ No icon mapping found for service: ${serviceName}`);
    return '/aws-icons/default.svg';
  }

  try {
    const fullUrl = `${S3_BUCKET_URL}${ICONS_BASE_PATH}/${iconPath}`;

    // Check if the resource exists
    const response = await fetch(fullUrl, { method: 'HEAD' });
    if (!response.ok) {
      throw new Error(`HTTP ${response.status}`);
    }

    iconResolutionLog.success.push({
      service: serviceName,
      path: iconPath
    });
    console.log(`âœ… Icon found for ${serviceName}: ${iconPath}`);
    return fullUrl;
  } catch (error) {
    iconResolutionLog.failed.push({
      service: serviceName,
      reason: error.message
    });
    console.error(`âŒ Error loading icon for ${serviceName}:`, error);
    return '/aws-icons/default.svg';
  }
};

// Add method to get resolution stats
const getIconResolutionStats = () => {
  const stats = {
    total: iconResolutionLog.total,
    success: iconResolutionLog.success.length,
    failed: iconResolutionLog.failed.length,
    successRate: `${((iconResolutionLog.success.length / iconResolutionLog.total) * 100).toFixed(1)}%`,
    failedServices: iconResolutionLog.failed.map(f => `${f.service} (${f.reason})`),
    successServices: iconResolutionLog.success.map(s => s.service)
  };

  console.group('ðŸŽ¯ Icon Resolution Statistics');
  console.log(`Total Services: ${stats.total}`);
  console.log(`Success: ${stats.success} (${stats.successRate})`);
  console.log(`Failed: ${stats.failed}`);
  console.log('\nSuccessfully resolved icons:');
  stats.successServices.forEach(s => console.log(`âœ… ${s}`));
  console.log('\nFailed to resolve icons:');
  stats.failedServices.forEach(s => console.log(`âŒ ${s}`));
  console.groupEnd();

  return stats;
};

// Modified cached version with logging
const iconCache = new Map();

const getCachedServiceIcon = async (serviceName) => {
  if (iconCache.has(serviceName)) {
    return iconCache.get(serviceName);
  }

  const iconUrl = await getServiceIcon(serviceName);
  iconCache.set(serviceName, iconUrl);
  return iconUrl;
};

export { getServiceIcon, getCachedServiceIcon, getIconResolutionStats };
