import React, { useState, useEffect, useCallback, useRef } from 'react';
import '../styles/GameCanvas.css';
import '../styles/IconsSelectCategoryFast.css';
import { getCategoryIcons, getRandomCategory } from '../utils/categorySelectionUtils';
import { getServiceIconUrl } from '../utils/memoryGameUtils';

const IconsSelectCategoryFast = () => {
  // Game state
  const [currentCategory, setCurrentCategory] = useState('');
  const [displayCategory, setDisplayCategory] = useState('');
  const [icons, setIcons] = useState([]);
  const [selectedIcons, setSelectedIcons] = useState([]);
  const [correctSelections, setCorrectSelections] = useState(0);
  const [timeElapsed, setTimeElapsed] = useState(0);
  const [score, setScore] = useState(0);
  const [gameStarted, setGameStarted] = useState(false);
  const [roundComplete, setRoundComplete] = useState(false);
  const [gameOver, setGameOver] = useState(false);
  const [showConfetti, setShowConfetti] = useState(false);
  const TIME_LIMIT = 60; // 60 seconds time limit per round

  // Sound state
  const [soundsLoaded, setSoundsLoaded] = useState(false);
  const [isMuted, setIsMuted] = useState(false);
  const sounds = useRef({});

  // Game container ref
  const gameContainerRef = useRef(null);
  const timerRef = useRef(null);
  const categoryIconsRef = useRef([]);

  // Player state & Welcome Modal State
  const [playerName, setPlayerName] = useState('');
  const [showWelcome, setShowWelcome] = useState(true);
  const [tempPlayerName, setTempPlayerName] = useState('');
  const [nameError, setNameError] = useState('');

  // Define sound files
  const soundFiles = {
    select: `${process.env.REACT_APP_AZURE_BUCKET_URL}/games/sounds/flip-switch.mp3`,
    correct: `${process.env.REACT_APP_AZURE_BUCKET_URL}/games/sounds/correct_sound.mp3`,
    incorrect: `${process.env.REACT_APP_AZURE_BUCKET_URL}/games/sounds/incorrect_sound.mp3`,
    hover: `${process.env.REACT_APP_AZURE_BUCKET_URL}/games/sounds/hover.mp3`,
    achievement: `${process.env.REACT_APP_AZURE_BUCKET_URL}/games/sounds/levelup.mp3`
  };

  // Helper function to safely create audio objects with timeout
  const createSafeAudio = useCallback((path) => {
    const audio = new Audio();
    return new Promise((resolve) => {
      audio.addEventListener('error', () => {
        console.warn(`Sound file not found: ${path}`);
        resolve(null);
      });
      audio.addEventListener('canplaythrough', () => {
        resolve(audio);
      });
      // Timeout fallback in case sound loading takes too long
      setTimeout(() => {
        if (audio.readyState < 3) {
          console.warn(`Sound loading timeout: ${path}`);
          resolve(null);
        }
      }, 3000);
      audio.src = path;
    });
  }, []);

  // Load all sounds
  useEffect(() => {
    const loadSounds = async () => {
      const soundPromises = Object.entries(soundFiles).map(async ([name, path]) => {
        try {
          const audio = await createSafeAudio(path);
          return [name, audio];
        } catch (error) {
          console.error(`Failed to load sound ${name}:`, error);
          return [name, null];
        }
      });

      try {
        const loadedSounds = await Promise.all(soundPromises);
        const soundsMap = Object.fromEntries(loadedSounds);
        sounds.current = soundsMap;

        // Check if at least some sounds loaded successfully
        const hasLoadedSounds = Object.values(soundsMap).some(sound => sound !== null);
        setSoundsLoaded(hasLoadedSounds);

        if (hasLoadedSounds) {
          console.log('üîä Sounds loaded successfully');
        } else {
          console.warn('‚ö†Ô∏è No sounds could be loaded');
        }
      } catch (error) {
        console.error('Failed to load sounds:', error);
      }
    };

    loadSounds();
  }, [createSafeAudio]);

  // Toggle mute
  const toggleMute = useCallback(() => {
    setIsMuted(!isMuted);
  }, [isMuted]);

  // Play a sound effect
  const playSound = useCallback(
    (soundName) => {
      if (isMuted || !sounds.current[soundName]) return;
      const sound = sounds.current[soundName];
      sound.currentTime = 0;
      sound.play().catch(err => console.warn('Error playing sound:', err));
    },
    [isMuted]
  );

  // Initialize a round (called by startGame and nextCategory)
  const initRound = useCallback(() => {
    const { category, displayName, icons: allIcons, targetIcons } = getRandomCategory();

    setCurrentCategory(category);
    setDisplayCategory(displayName);
    setIcons(allIcons);
    categoryIconsRef.current = targetIcons;
    setSelectedIcons([]);
    setCorrectSelections(0);
    setTimeElapsed(0);
    setRoundComplete(false);
    setGameOver(false);
    setShowConfetti(false);

    // Clear any existing timer
    if (timerRef.current) {
      clearInterval(timerRef.current);
    }

    // Start timer only if game has officially started (name entered)
    if (gameStarted) {
      const startTime = Date.now();
      timerRef.current = setInterval(() => {
        const newTime = Math.floor((Date.now() - startTime) / 1000);
        setTimeElapsed(newTime);

        // Check if time has run out
        if (newTime >= TIME_LIMIT) {
          if (timerRef.current) {
            clearInterval(timerRef.current);
          }
          // Only set gameOver if the round wasn't already completed successfully
          if (!roundComplete) {
            setGameOver(true);
            playSound('incorrect');
          }
        }
      }, 1000);
    }

    console.log(`Starting new round with category: ${displayName}`);
    console.log(`Target icons:`, targetIcons);
  }, [gameStarted, roundComplete, playSound]);

  // Next category logic
  const nextCategory = useCallback(() => {
    // Stop any confetti
    setShowConfetti(false);

    // Reset game state for next round using initRound
    initRound();

    // Play sound effect
    playSound('select');
  }, [initRound, playSound]);

  // Handle round completion
  const handleRoundComplete = useCallback(() => {
    if (timerRef.current) {
      clearInterval(timerRef.current);
    }

    setRoundComplete(true);
    setShowConfetti(true);
    playSound('achievement');

    // Calculate score based on time
    const timeBonus = Math.max(100 - timeElapsed * 2, 10);
    setScore(prev => prev + timeBonus);

    // Automatically move to the next category after a delay
    setTimeout(() => {
      nextCategory();
    }, 3000); // 3 second delay
  }, [timeElapsed, playSound, nextCategory]);

  // Handle card click - Updated Scoring
  const handleCardClick = (iconName) => {
    if (roundComplete || gameOver) return; // Don't allow clicks if round/game is over

    playSound('select');

    const isTarget = categoryIconsRef.current.includes(iconName);

    if (selectedIcons.includes(iconName)) {
      // Deselecting
      setSelectedIcons(prev => prev.filter(i => i !== iconName));
      if (isTarget) {
        setCorrectSelections(prev => prev - 1);
        // Optional: Penalize for deselecting a correct card? Current logic doesn't.
      }
    } else {
      // Selecting
      setSelectedIcons(prev => [...prev, iconName]);

      if (isTarget) {
        // Correct selection
        const newCorrectSelections = correctSelections + 1;
        setCorrectSelections(newCorrectSelections);
        playSound('correct');
        setScore(prev => prev + 5); // Add 5 points for correct selection

        // Check if all correct icons are selected
        if (newCorrectSelections === categoryIconsRef.current.length) {
          handleRoundComplete();
        }
      } else {
        // Incorrect selection
        playSound('incorrect');
        setScore(prev => Math.max(0, prev - 5)); // Penalty of 5 points
      }
    }
  };

  // Start the actual game after name submission
  const startGame = useCallback(() => {
    setShowWelcome(false);
    setGameStarted(true); // Mark game as officially started
    setScore(0); // Reset score when starting fresh
    initRound(); // Initialize the first round
  }, [initRound]);

  // Handle Name Submission from Welcome Modal
  const handleNameSubmit = (e) => {
    e.preventDefault();
    const trimmedName = tempPlayerName.trim();
    if (trimmedName.length < 2) {
      setNameError('Name must be at least 2 characters long');
      return;
    }
    if (trimmedName.length > 15) {
      setNameError('Name must be less than 15 characters');
      return;
    }
    setPlayerName(trimmedName);
    // Optionally save to localStorage if needed: localStorage.setItem('playerName', trimmedName);
    setNameError('');
    startGame(); // Call startGame to hide modal and init the first round
  };

  // Effect to handle game over state (when timer runs out)
  useEffect(() => {
    let timer;
    if (gameOver && !roundComplete) { // Only trigger if game over due to time, not completion
      // Show failure message, maybe wait a bit before next round
      timer = setTimeout(() => {
        nextCategory(); // Move to the next category/round after timeout
      }, 3000); // 3 second delay
    }
    return () => {
      if (timer) clearTimeout(timer);
    };
  }, [gameOver, roundComplete, nextCategory]);

  // Cleanup timer on unmount
  useEffect(() => {
    return () => {
      if (timerRef.current) {
        clearInterval(timerRef.current);
      }
    };
  }, []);

  // Format time as MM:SS
  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins < 10 ? '0' : ''}${mins}:${secs < 10 ? '0' : ''}${secs}`;
  };

  // Confetti component
  const Confetti = ({ active }) => {
    if (!active) return null;

    const confettiCount = 150;
    const confetti = [];

    for (let i = 0; i < confettiCount; i++) {
      const left = `${Math.random() * 100}%`;
      const animationDelay = `${Math.random() * 5}s`;
      const initialOpacity = Math.random();
      const size = `${Math.random() * 10 + 5}px`;
      const rotation = `${Math.random() * 360}deg`;
      const color = [
        '#00ffff', // Cyan (Azure color)
        '#0078d4', // Blue (Azure color)
        '#ffb900', // Gold
        '#e74c3c', // Red
        '#2ecc71', // Green
        '#9b59b6', // Purple
        '#3498db', // Light blue
      ][Math.floor(Math.random() * 7)];

      confetti.push(
        <div 
          key={i}
          className="memory-confetti-particle"
          style={{
            left,
            top: '-10px',
            width: size,
            height: size,
            backgroundColor: color,
            transform: `rotate(${rotation})`,
            opacity: initialOpacity,
            animationDelay,
          }}
        />
      );
    }

    return <div className="memory-confetti-container">{confetti}</div>;
  };

  // Show Welcome Modal if showWelcome is true
  if (showWelcome) {
    return (
      <div className="gameCanvas-modal-overlay">
        <div className="gameCanvas-modal">
          <h2>Welcome to Azure Category Challenge!</h2>
          <p>Enter your name to start the game:</p>
          <form onSubmit={handleNameSubmit}>
            <input
              type="text"
              value={tempPlayerName}
              onChange={(e) => {
                setTempPlayerName(e.target.value);
                setNameError(''); // Clear error on change
              }}
              placeholder="Your name"
              className="gameCanvas-name-input"
              autoFocus
            />
            {nameError && <div className="gameCanvas-name-error">{nameError}</div>}
            <button type="submit" className="gameCanvas-start-button">
              Start Game
            </button>
          </form>
        </div>
      </div>
    );
  }

  // Main Game UI
  return (
    <div 
      className="gameCanvas-container" 
      ref={gameContainerRef}
    >
      <Confetti active={showConfetti} />

      <h1 
        className="gameCanvas-game-title"
        style={{ '--azure-icon': `url(${process.env.PUBLIC_URL}/azure-icons/Microsoft_Azure_Cyan.svg)` }}
      >
        Azure Category Challenge
      </h1>

      <div className="gameCanvas-game-content">
        <div className="gameCanvas-canvas-wrapper" style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'flex-start' }}>
          <div className="category-selection-header">
            <h2 className="category-selection-instruction">
              Select all cards for: <span className="category-highlight">{displayCategory}</span>
            </h2>
            <p className="category-selection-progress">
              Found: {correctSelections} / {categoryIconsRef.current.length}
            </p>
          </div>

          {soundsLoaded && (
            <button 
              className="memory-game-sound-toggle top-right"
              onClick={toggleMute}
              aria-label="Toggle sound"
            >
              {isMuted ? 'üîá' : 'üîä'}
            </button>
          )}

          <div className="gameCanvas-player-panel">
            <div className="gameCanvas-player-info">
              <div className="gameCanvas-player-name">
                Player: {playerName}
              </div>
              <div className="gameCanvas-score-display">
                <span role="img" aria-label="alien">üëæ</span>{' '}
                Score: {score}{' '}
                <span role="img" aria-label="joystick">üïπÔ∏è</span>
              </div>
            </div>
            <div className="gameCanvas-timer-display">
              Time: {formatTime(TIME_LIMIT - timeElapsed)}
            </div>
          </div>

          <div className="category-selection-grid">
            {icons.map((icon, index) => (
              <div
                key={`${icon.name}-${index}`}
                className={`category-selection-card ${selectedIcons.includes(icon.name) ? 
                  (categoryIconsRef.current.includes(icon.name) ? 'correct' : 'wrong') : ''} ${roundComplete || gameOver ? 'disabled' : ''}`}
                onClick={() => handleCardClick(icon.name)}
                onMouseEnter={() => !roundComplete && !gameOver && playSound('hover')}
              >
                <img 
                  src={getServiceIconUrl(icon.path)} 
                  alt={icon.name} 
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.src = `${process.env.PUBLIC_URL}/azure-icons/default.svg`;
                  }}
                />
                <div className="category-selection-card-text">{icon.name}</div>
              </div>
            ))}
          </div>

          {roundComplete && (
            <div className="category-selection-message category-selection-success">
              <h3>Great job! You found all {categoryIconsRef.current.length} icons in {timeElapsed} seconds.</h3>
              <p>Time Bonus: +{Math.max(100 - timeElapsed * 2, 10)} points!</p>
              <p>Next round starting soon...</p>
            </div>
          )}

          {gameOver && !roundComplete && (
            <div className="category-selection-message category-selection-failure-banner">
              <h3>Time's up!</h3>
              <p>You found {correctSelections} out of {categoryIconsRef.current.length} icons for {displayCategory}.</p>
              <p>Next round starting soon...</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default IconsSelectCategoryFast;
