import React, { useState, useCallback, useRef, useEffect } from 'react';
import GameCanvas from './GameCanvas';
import { Link } from 'react-router-dom';
import CosmosDBService from '../services/CosmosDBService';

const CloudShooter = () => {
  // Game state
  const [playerName, setPlayerName] = useState('');
  const [score, setScore] = useState(0);
  const [leaderboard, setLeaderboard] = useState([]);
  const [showWelcome, setShowWelcome] = useState(true);
  const [gameStarted, setGameStarted] = useState(false);
  const gameType = 'CloudShooter';

  // Fetch leaderboard on component mount and when game starts
  useEffect(() => {
    fetchLeaderboard();
  }, []);

  // Add score saving function
  const saveScore = async () => {
    console.log('üéØ Attempting to save score:', {
      playerName,
      gameType,
      score
    });

    if (!playerName || score === 0) {
      console.warn('‚ùå Score save prevented:', {
        playerName,
        score
      });
      return;
    }

    try {
      const result = await CosmosDBService.saveGameScore(playerName, gameType, score);
      console.log('‚úÖ Score saved successfully:', result);
      // Fetch updated leaderboard after saving score
      await fetchLeaderboard();
    } catch (error) {
      console.error('‚ùå Error saving score:', error);
    }
  };

  // Add leaderboard fetching function
  const fetchLeaderboard = async () => {
    console.log('üèÜ Fetching leaderboard for:', gameType);
    try {
      const data = await CosmosDBService.getLeaderboard(gameType);
      console.log('‚úÖ Leaderboard fetched:', data);
      setLeaderboard(data);
      return data;
    } catch (error) {
      console.error('‚ùå Error fetching leaderboard:', error);
      return [];
    }
  };

  // Add leaderboard display component
  const LeaderboardDisplay = () => (
    <div className="gameCanvas-high-scores" style={{
      backgroundColor: 'rgba(0, 40, 56, 0.8)',
      border: '1px solid #00ffff',
      borderRadius: '8px',
      padding: '15px',
      marginTop: '10px',
      position: 'absolute',
      right: '20px',
      top: '100px',
      width: '200px'
    }}>
      <h4 style={{ 
        color: '#00ffff',
        marginBottom: '10px',
        textAlign: 'center'
      }}>
        HIGH SCORES
      </h4>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {leaderboard.map((entry, index) => (
          <li key={index} style={{ 
            display: 'flex', 
            justifyContent: 'space-between',
            padding: '4px 0',
            color: '#fff'
          }}>
            <span>{entry.nickname}:</span>
            <span>{entry.score}</span>
          </li>
        ))}
      </ul>
    </div>
  );

  // Sound state
  const [isMuted, setIsMuted] = useState(false);
  const sounds = useRef({});

  // Define sound files
  const soundFiles = {
    hover: `${process.env.REACT_APP_AZURE_BUCKET_URL}/games/sounds/hover.mp3`
  };

  // Play sound function
  const playSound = useCallback((soundName) => {
    if (!sounds.current[soundName] && soundFiles[soundName]) {
      sounds.current[soundName] = new Audio(soundFiles[soundName]);
    }

    const sound = sounds.current[soundName];
    if (sound && !isMuted) {
      try {
        sound.currentTime = 0;
        sound.volume = 0.3;
        sound.play().catch(err => {
          console.warn(`Error playing sound ${soundName}:`, err);
        });
      } catch (err) {
        console.warn(`Error with sound ${soundName}:`, err);
      }
    }
  }, [isMuted]);

  return (
    <div id="azure-cloud-shooter">
      <GameCanvas 
        onScoreUpdate={setScore}
        onPlayerNameChange={setPlayerName}
        onGameOver={saveScore}
        leaderboard={leaderboard}
        fetchLeaderboard={fetchLeaderboard}
        showWelcome={showWelcome}
        setShowWelcome={setShowWelcome}
        gameStarted={gameStarted}
        setGameStarted={setGameStarted}
      />
      <h1 className="gameCanvas-game-title">AZURE CLOUD SHOOTER</h1>
      <LeaderboardDisplay />

      {/* Back to Home button */}
      <Link 
        to="/" 
        className="back-to-home-button"
        style={{
          position: 'absolute',
          top: '20px',
          left: '20px',
          display: 'flex',
          alignItems: 'center',
          padding: '10px 20px',
          backgroundColor: 'rgba(0, 255, 255, 0.1)',
          border: '2px solid #00ffff',
          borderRadius: '5px',
          color: '#00ffff',
          textDecoration: 'none',
          fontFamily: 'Orbitron, sans-serif',
          fontSize: '14px',
          transition: 'all 0.3s ease',
          zIndex: 100
        }}
        onMouseEnter={() => playSound('hover')}
      >
        <svg 
          xmlns="http://www.w3.org/2000/svg" 
          viewBox="0 0 24 24" 
          fill="currentColor" 
          style={{
            width: '20px',
            height: '20px',
            marginRight: '8px'
          }}
        >
          <path d="M12 2L1 12h3v9h6v-6h4v6h6v-9h3L12 2z"/>
        </svg>
        Back to Home
      </Link>
    </div>
  );
};

export default CloudShooter;
