import OpenAI from 'openai';

const openai = new OpenAI({
  apiKey: process.env.REACT_APP_OPENAI_API_KEY,
  dangerouslyAllowBrowser: true
});

const AVAILABLE_SERVICES = [
  { id: 'lambda', name: 'AWS Lambda', icon: '/aws-icons/lambda.png' },
  { id: 's3', name: 'Amazon S3', icon: '/aws-icons/s3.png' },
  { id: 'ec2', name: 'Amazon EC2', icon: '/aws-icons/ec2.png' },
  { id: 'ecs', name: 'Amazon ECS', icon: '/aws-icons/ecs.png' },
  { id: 'dynamodb', name: 'Amazon DynamoDB', icon: '/aws-icons/dynamodb.png' },
  { id: 'rds', name: 'Amazon RDS', icon: '/aws-icons/rds.png' }
];

const generateArchitecture = async (difficulty) => {
  const complexityByDifficulty = {
    'BEGINNER': { minServices: 3, maxServices: 4 },
    'INTERMEDIATE': { minServices: 4, maxServices: 6 },
    'ADVANCED': { minServices: 6, maxServices: 8 }
  };

  try {
    const completion = await openai.chat.completions.create({
      model: "gpt-4",
      messages: [
        { 
          role: "system", 
          content: `You are an AWS architecture expert. Generate a valid JSON response with no additional text or formatting. Use only these services: ${AVAILABLE_SERVICES.map(s => s.id).join(', ')}.

Example response format:
{
  "services": ["lambda", "s3", "dynamodb"],
  "connections": [
    {"from": "lambda", "to": "dynamodb"},
    {"from": "s3", "to": "lambda"}
  ],
  "missingService": "rds",
  "question": "Which database service would you add to store relational data?",
  "options": ["rds", "dynamodb", "s3", "ec2"]
}

For difficulty level ${difficulty}, use ${complexityByDifficulty[difficulty].minServices}-${complexityByDifficulty[difficulty].maxServices} services.`
        },
        {
          role: "user",
          content: "Generate an AWS architecture"
        }
      ],
      temperature: 0.7
    });

    const responseText = completion.choices[0].message.content.trim();
    const aiSuggestion = JSON.parse(responseText);
    console.log('AI Response:', aiSuggestion);

    // Map service IDs to full service objects
    const getServiceById = (id) => AVAILABLE_SERVICES.find(s => s.id === id) || AVAILABLE_SERVICES[0];

    return {
      architecture: {
        services: aiSuggestion.services.map(id => getServiceById(id)),
        connections: aiSuggestion.connections || []
      },
      question: {
        text: aiSuggestion.question || 'Which service would you add?',
        missingServices: [aiSuggestion.missingService]
      },
      options: aiSuggestion.options.map(id => getServiceById(id))
    };

  } catch (error) {
    console.error('AI Error:', error);
    // Fallback with more complex randomization
    const randomServices = AVAILABLE_SERVICES.sort(() => 0.5 - Math.random()).slice(0, 4);

    return {
      architecture: {
        services: randomServices.slice(0, 3),
        connections: [
          { from: randomServices[0].id, to: randomServices[1].id },
          { from: randomServices[1].id, to: randomServices[2].id }
        ]
      },
      question: {
        text: `Which AWS service would you use to enhance this architecture?`,
        missingServices: [randomServices[3].id]
      },
      options: AVAILABLE_SERVICES.slice(0, 4)
    };
  }
};

export { generateArchitecture };
