import React, { useState, useCallback } from 'react';
import { DndContext, useSensor, useSensors, PointerSensor, TouchSensor } from '@dnd-kit/core';
import AWSArchitectureDisplay from './AWSArchitectureDisplay';
import DraggableService from './DraggableService';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import { getCachedServiceIcon, SERVICE_MAPPINGS } from '../services/IconResolver';

const S3_BUCKET_URL = 'https://hackthon-backend-files-ep-2024.s3.amazonaws.com';
const ICONS_BASE_PATH = '/amazon-icons-set/Architecture-Service-Icons_06072024';

const AWSArchitectureGame = () => {
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);
    const [architecture, setArchitecture] = useState(null);
    const [architectureInfo, setArchitectureInfo] = useState(null);
    const [currentQuestion, setCurrentQuestion] = useState(null);
    const [serviceOptions, setServiceOptions] = useState([]);
    const [difficulty] = useState('BEGINNER');
    const [score, setScore] = useState(0); 
    const [showFeedback, setShowFeedback] = useState(false);
    const [feedback, setFeedback] = useState({ isCorrect: false, message: '', points: 0 });
    const [currentMissingServiceIndex, setCurrentMissingServiceIndex] = useState(0);

    const API_URL = 'https://p7bwtrx47e.execute-api.us-east-1.amazonaws.com/stage/amazon-architecture';

    const sensors = useSensors(
      useSensor(PointerSensor, {
        activationConstraint: {
          distance: 8,
        },
      }),
      useSensor(TouchSensor)
    );

    const loadNextArchitecture = useCallback(async () => {
      if (isLoading) return;
      setIsLoading(true);
      setError(null);

      try {
        console.log('üöÄ Fetching new architecture...');
        const response = await fetch(API_URL, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Accept': 'application/json',
            'Origin': 'http://localhost:3000'
          },
          mode: 'cors',
          credentials: 'omit',
          body: JSON.stringify({ difficulty })
        });

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();
        console.log('üì¶ Received data:', data);

        // Set architecture info
        setArchitectureInfo({
          name: data.architecture.name,
          description: data.architecture.description
        });

        // Transform services with proper error handling
        const transformedServices = (data.services || []).map(serviceName => {
          if (!serviceName) {
            console.warn('‚ö†Ô∏è Undefined service name encountered');
            return null;
          }
          return {
            id: serviceName.toLowerCase().replace(/[^a-z0-9]+/g, '-'),
            name: serviceName,
            icon: serviceName.toLowerCase().includes('missing') ? 
              '/aws-icons/missing.svg' : 
              getCachedServiceIcon(serviceName)
          };
        }).filter(Boolean);

        // Create service name to ID mapping
        const serviceNameToId = {};
        transformedServices.forEach(service => {
          if (service && service.name) {
            serviceNameToId[service.name] = service.id;
          }
        });

        // Transform connections
        const transformedConnections = (data.connections || [])
          .filter(conn => conn && conn.from && conn.to)
          .map(conn => ({
            from: serviceNameToId[conn.from] || conn.from.toLowerCase().replace(/[^a-z0-9]+/g, '-'),
            to: serviceNameToId[conn.to] || conn.to.toLowerCase().replace(/[^a-z0-9]+/g, '-')
          }));

        setArchitecture({
          services: transformedServices,
          connections: transformedConnections
        });

        // Handle missing services and questions
        if (data.missingServices && data.missingServices.length > 0) {
          const currentMissingService = data.missingServices[0];
          setCurrentQuestion({
            text: currentMissingService.question,
            optimizationFocus: currentMissingService.optimizationFocus,
            position: currentMissingService.position,
            options: currentMissingService.options.map(opt => ({
              id: opt.service.toLowerCase().replace(/\s+/g, '-'),
              name: opt.service,
              isCorrect: opt.isCorrect,
              isOptimal: opt.isOptimal,
              explanation: opt.explanation,
              rating: opt.rating,
              icon: opt.service.toLowerCase().includes('missing') ? 
                '/aws-icons/missing.svg' : 
                getCachedServiceIcon(opt.service)
            }))
          });
        }
      } catch (error) {
        console.error('‚ùå Failed to load architecture:', error);
        setError(error.message);
      } finally {
        setIsLoading(false);
      }
    }, [API_URL, difficulty, isLoading]);

    const handleServiceSelection = (selectedService) => {
      const currentMissingService = architectureInfo.missingServices[currentMissingServiceIndex];
      const selectedOption = currentMissingService.options.find(opt => opt.service === selectedService);

      setShowFeedback(true);
      if (selectedOption.isCorrect) {
        const points = selectedOption.rating;
        setScore(prevScore => prevScore + points);
        setFeedback({
          isCorrect: true,
          message: selectedOption.explanation,
          points: points,
          isOptimal: selectedOption.isOptimal
        });
      } else {
        setFeedback({
          isCorrect: false,
          message: selectedOption.explanation,
          points: 0
        });
      }
    };

    const handleNext = () => {
      setShowFeedback(false);
      if (currentMissingServiceIndex < architectureInfo.missingServices.length - 1) {
        setCurrentMissingServiceIndex(prev => prev + 1);
      }
    };

    return (
      <DndProvider backend={HTML5Backend}>
        <div className="game-container">
          <div className="game-header">
            <h2>{architectureInfo?.name || 'AWS Architecture Challenge'}</h2>
            <div className="score">Score: {score}</div>
            <button 
              className="new-architecture-button"
              onClick={loadNextArchitecture}
              disabled={isLoading}
            >
              {isLoading ? 'Loading...' : 'New Architecture'}
            </button>
          </div>

          {architectureInfo && <p className="architecture-description">{architectureInfo.description}</p>}

          {error ? (
            <div className="error-message">{error}</div>
          ) : (
            <>
              <AWSArchitectureDisplay 
                architecture={architecture}
                currentQuestion={currentQuestion}
              />

              {currentQuestion && (
                <div className="question-container">
                  <h3>Question: {currentQuestion.text}</h3>
                  <p>Optimization Focus: {currentQuestion.optimizationFocus}</p>

                  {showFeedback ? (
                    <div className={`feedback-modal ${feedback.isCorrect ? 'correct' : 'incorrect'}`}>
                      <div className="feedback-content">
                        {feedback.isCorrect ? (
                          <>
                            <h3>üéâ Correct! +{feedback.points} points</h3>
                            {feedback.isOptimal && <p>üåü Perfect choice! This is the optimal solution.</p>}
                          </>
                        ) : (
                          <h3>‚ùå Not quite right</h3>
                        )}
                        <p>{feedback.message}</p>

                        {currentMissingServiceIndex < architectureInfo.missingServices.length - 1 && (
                          <button 
                            className="next-button"
                            onClick={handleNext}
                          >
                            Next Missing Service ({currentMissingServiceIndex + 2} of {architectureInfo.missingServices.length})
                          </button>
                        )}
                      </div>
                    </div>
                  ) : (
                    <div className="service-options">
                      {currentQuestion.options?.map((option, index) => (
                        <DraggableService
                          key={`${option.id}-${index}`}
                          service={option}
                          onClick={() => handleServiceSelection(option.name)}
                        />
                      ))}
                    </div>
                  )}
                </div>
              )}
            </>
          )}

          <div className="game-status">
            <div className="progress">
              Service {currentMissingServiceIndex + 1} of {architectureInfo?.missingServices?.length || 0}
            </div>
          </div>
        </div>
      </DndProvider>
    );
};

export default AWSArchitectureGame;
