import React from 'react';
import ReactFlow, { 
  Background, 
  Controls, 
  MiniMap,
  useNodesState,
  useEdgesState,
  MarkerType,
  Handle
} from 'reactflow';
import 'reactflow/dist/style.css';
import { motion } from 'framer-motion';
import { getCachedServiceIcon } from '../services/IconResolver';

const AWSServiceNode = ({ data }) => (
  <motion.div
    className={`aws-service-node ${data.isMissing ? 'missing' : ''}`}
    initial={{ scale: 0, opacity: 0 }}
    animate={{ scale: 1, opacity: 1 }}
    exit={{ scale: 0, opacity: 0 }}
    whileHover={{ scale: 1.1 }}
    transition={{ type: "spring", stiffness: 300 }}
  >
    <Handle 
      type="target" 
      position="top" 
      id={`${data.id}-target`}
      className="handle"
    />
    <div className="node-content">
      {data.isMissing ? (
        <div className="missing-service-placeholder">
          <span>?</span>
        </div>
      ) : (
        <img src={data.icon} alt={data.label} className="service-icon" />
      )}
      <span className="service-label">{data.label}</span>
    </div>
    <Handle 
      type="source" 
      position="bottom" 
      id={`${data.id}-source`}
      className="handle"
    />
  </motion.div>
);

const nodeTypes = {
  awsService: AWSServiceNode
};

const createNodeId = (serviceName) => {
  if (typeof serviceName !== 'string') {
    console.error('Invalid service name:', serviceName);
    return 'invalid-service';
  }
  return serviceName.toLowerCase().replace(/\s+/g, '-');
};

const AWSArchitectureDisplay = ({ architecture }) => {
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);

  React.useEffect(() => {
    if (!architecture?.services) {
      console.log('âš ï¸ Waiting for architecture data...');
      return;
    }

    console.log('ðŸ“Š Rendering architecture:', {
      servicesCount: architecture.services.length,
      connectionsCount: architecture.connections?.length
    });

    const newNodes = [];
    const newEdges = [];

    // Add nodes
    architecture.services.forEach((service, index) => {
      const serviceId = createNodeId(service);
      const isMissing = service.startsWith('missing_');

      newNodes.push({
        id: serviceId,
        type: 'awsService',
        position: { x: index * 200, y: 200 },
        data: {
          label: isMissing ? `Missing Service ${service}` : service,
          icon: isMissing ? null : getCachedServiceIcon(service),
          isMissing
        }
      });
    });

    // Ensure missing_x nodes are correctly connected
    architecture.connections.forEach((conn) => {
      const fromId = createNodeId(conn.from);
      const toId = createNodeId(conn.to);

      newEdges.push({
        id: `${fromId}-${toId}`,
        source: fromId,
        target: toId,
        type: 'smoothstep',
        animated: true,
        style: { stroke: '#4FD1C5', strokeWidth: 2 },
        markerEnd: { type: MarkerType.ArrowClosed }
      });
    });

    setNodes(newNodes);
    setEdges(newEdges);
  }, [architecture, setNodes, setEdges]);

  return (
    <div style={{ height: '500px' }} className="architecture-visualizer">
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        nodeTypes={nodeTypes}
        fitView
        minZoom={0.5}
        maxZoom={1.5}
      >
        <Background color="#4FD1C5" gap={16} size={1} />
        <Controls />
        <MiniMap 
          nodeColor={(node) => node.data.isMissing ? '#FF5656' : '#4FD1C5'}
          maskColor="rgba(13, 25, 35, 0.7)"
        />
      </ReactFlow>
    </div>
  );
};

export default AWSArchitectureDisplay;
