import React, { useState, useCallback, useEffect } from 'react';
import AWSArchitectureDisplay from './AWSArchitectureDisplay';
import { getCachedServiceIcon } from '../services/IconResolver';

const API_URL = 'https://p7bwtrx47e.execute-api.us-east-1.amazonaws.com/stage/amazon-architecture';

/**
 * Validate the JSON response structure to ensure it includes required missing services.
 * @param {Object} data - JSON response from the API.
 * @returns {boolean} - Returns true if the JSON is valid, otherwise false.
 */
const isValidArchitecture = (data) => {
  if (!data || !Array.isArray(data.services) || !Array.isArray(data.connections)) {
    console.warn("âš ï¸ Invalid JSON structure: Missing 'services' or 'connections'. Retrying...");
    return false;
  }

  // Required missing services
  const requiredMissingServices = ['missing_1', 'missing_2', 'missing_3'];

  // Extract service names (handles both strings and objects)
  const serviceNames = data.services.map(service => 
    typeof service === 'string' ? service : service?.name
  );

  // Ensure all missing services exist in the services list
  const allMissingServicesExist = requiredMissingServices.every(ms => serviceNames.includes(ms));

  if (!allMissingServicesExist) {
    console.warn(`âš ï¸ JSON is invalid: One or more missing services (${requiredMissingServices.join(', ')}) are missing.`);
    return false;
  }

  // Ensure missing services are referenced in the connections
  const connectionNodes = new Set();
  data.connections.forEach(conn => {
    connectionNodes.add(conn.from);
    connectionNodes.add(conn.to);
  });

  const allMissingServicesConnected = requiredMissingServices.every(ms => connectionNodes.has(ms));

  if (!allMissingServicesConnected) {
    console.warn(`âš ï¸ JSON is invalid: One or more missing services (${requiredMissingServices.join(', ')}) are missing from the connections.`);
    return false;
  }

  return true; // JSON is valid
};

const AWSArchitectureGame = () => {
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [architecture, setArchitecture] = useState(null);
  const [architectureInfo, setArchitectureInfo] = useState(null);
  const [currentQuestion, setCurrentQuestion] = useState(null);
  const [serviceOptions, setServiceOptions] = useState([]);
  const [difficulty] = useState('BEGINNER');

  /**
   * Fetch architecture data, retrying until the JSON is valid.
   */
  const loadNextArchitecture = useCallback(async () => {
    if (isLoading) return;
    setIsLoading(true);
    setError(null);

    let isValid = false;
    let data = null;
    let attempt = 0;

    while (!isValid && attempt < 5) { // Max 5 retries
      try {
        console.log(`ðŸ”„ Attempt ${attempt + 1}: Fetching architecture data...`);
        const response = await fetch(API_URL, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ difficulty })
        });

        if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);

        data = await response.json();
        console.log("ðŸ“¥ Received AI Response:", JSON.stringify(data, null, 2));

        // Validate the JSON structure
        isValid = isValidArchitecture(data);

        if (!isValid) {
          console.warn(`â›” Invalid JSON structure. Retrying request...`);
        }

      } catch (error) {
        console.error("âŒ Failed to load architecture:", error);
        setError(error.message);
        setIsLoading(false);
        return;
      }

      attempt++;
    }

    if (!isValid) {
      console.error("âŒ Maximum retries reached. Unable to get valid JSON.");
      setError("Failed to fetch a valid architecture after multiple attempts.");
      setIsLoading(false);
      return;
    }

    console.log("âœ… Valid architecture received, updating state...");

    // Transform services with proper icon handling
    const transformedServices = data.services.map(serviceName => {
      if (!serviceName) {
        console.error("âš ï¸ Undefined service in services array");
        return null;
      }

      return {
        id: serviceName.toLowerCase().replace(/[^a-z0-9]+/g, '-'),
        name: serviceName,
        icon: getCachedServiceIcon(serviceName)
      };
    }).filter(Boolean);

    setArchitecture({
      services: transformedServices,
      connections: data.connections
    });

    if (data.missingServices?.length > 0) {
      const currentMissingService = data.missingServices[0];
      setCurrentQuestion({
        text: currentMissingService.question,
        optimizationFocus: currentMissingService.optimizationFocus,
        options: currentMissingService.options.map(opt => {
          if (!opt.service) {
            console.error("âš ï¸ Undefined service in options:", opt);
            return null;
          }

          return {
            id: opt.service.toLowerCase().replace(/\s+/g, '-'),
            name: opt.service,
            isCorrect: opt.isCorrect,
            isOptimal: opt.isOptimal,
            explanation: opt.explanation,
            rating: opt.rating,
            icon: getCachedServiceIcon(opt.service)
          };
        }).filter(Boolean)
      });
    }

    setArchitectureInfo({
      name: data.architecture.name,
      description: data.architecture.description,
      missingServices: data.missingServices
    });

    setIsLoading(false);
  }, [API_URL, difficulty, isLoading]);

  useEffect(() => {
    loadNextArchitecture();
  }, [loadNextArchitecture]);

  return (
    <div className="game-container">
      <div className="game-header">
        <h2>{architectureInfo?.name || 'AWS Architecture Challenge'}</h2>
        <button 
          className="new-architecture-button"
          onClick={loadNextArchitecture}
          disabled={isLoading}
        >
          {isLoading ? 'Loading...' : 'New Architecture'}
        </button>
      </div>

      {architectureInfo && <p className="architecture-description">{architectureInfo.description}</p>}

      {error ? (
        <div className="error-message">{error}</div>
      ) : (
        <AWSArchitectureDisplay architecture={architecture} />
      )}
    </div>
  );
};

export default AWSArchitectureGame;
