import React from 'react';
import ReactFlow, {
  Background,
  Controls,
  MiniMap,
  useNodesState,
  useEdgesState,
  MarkerType,
  Handle,
} from 'reactflow';
import 'reactflow/dist/style.css';
import { motion } from 'framer-motion';
import { getCachedServiceIcon } from '../services/IconResolver';

const AWSServiceNode = ({ data }) => (
  <motion.div
    className={`aws-service-node ${data.isMissing ? 'missing' : ''}`} // Corrected class name
    initial={{ scale: 0, opacity: 0 }}
    animate={{ scale: 1, opacity: 1 }}
    exit={{ scale: 0, opacity: 0 }}
    whileHover={{ scale: 1.1 }}
    transition={{ type: "spring", stiffness: 300 }}
  >
    <Handle type="target" position="top" id={`${data.id}-target`} className="handle" /> {/* Corrected template literal */}
    <div className="node-content">
      {data.isMissing ? (
        <div className="missing-service-placeholder">
          <span>?</span>
        </div>
      ) : (
        <img src={data.icon} alt={data.label} className="service-icon" />
      )}
      <span className="service-label">{data.label}</span>
    </div>
    <Handle type="source" position="bottom" id={`${data.id}-source`} className="handle" /> {/* Corrected template literal */}
  </motion.div>
);

const nodeTypes = {
  awsService: AWSServiceNode
};

const createNodeId = (serviceName) => {
  if (typeof serviceName !== 'string') {
    console.error('Invalid service name:', serviceName);
    return 'invalid-service';
  }
  return serviceName.toLowerCase().replace(/\s+/g, '-');
};

const AWSArchitectureDisplay = ({ architecture, missingServices }) => {
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);
  const hasLoggedWarning = React.useRef(false);

  React.useEffect(() => {
    if (!architecture?.services && !hasLoggedWarning.current) {
      hasLoggedWarning.current = true;
      console.warn('âš ï¸ Waiting for architecture data...'); // Changed to warn
      return;
    }

    if (architecture?.services) {
      console.log('ðŸ“Š Rendering architecture:', {
        servicesCount: architecture.services.length,
        connectionsCount: architecture.connections?.length
      });

      const newNodes = [];
      const newEdges = [];

      // Process connections and insert missing services
      (architecture.connections || []).forEach((conn) => {
        const fromId = createNodeId(conn.from);
        const toId = createNodeId(conn.to);

        const missingService = missingServices?.find(ms =>
          ms.position === fromId || ms.position === toId //Simplified check
        );

        if (missingService) {
          const placeholderId = `missing-${fromId}-${toId}`;
          newNodes.push({
            id: placeholderId,
            type: 'awsService',
            position: { x: newNodes.length * 200, y: 200 },
            data: { label: 'Missing Service', isMissing: true }
          });

          newEdges.push({
            id: `${fromId}-${placeholderId}`,
            source: fromId,
            target: placeholderId,
            type: 'smoothstep',
            animated: true,
            style: { stroke: '#4FD1C5', strokeWidth: 2 },
            markerEnd: { type: MarkerType.ArrowClosed },
            sourceHandle: `${fromId}-source`, //Added handle
            targetHandle: `${placeholderId}-target` //Added handle
          });
          newEdges.push({
            id: `${placeholderId}-${toId}`,
            source: placeholderId,
            target: toId,
            type: 'smoothstep',
            animated: true,
            style: { stroke: '#4FD1C5', strokeWidth: 2 },
            markerEnd: { type: MarkerType.ArrowClosed },
            sourceHandle: `${placeholderId}-source`, //Added handle
            targetHandle: `${toId}-target` //Added handle
          });
        } else {
          newEdges.push({
            id: `${fromId}-${toId}`,
            source: fromId,
            target: toId,
            type: 'smoothstep',
            animated: true,
            style: { stroke: '#4FD1C5', strokeWidth: 2 },
            markerEnd: { type: MarkerType.ArrowClosed },
            sourceHandle: `${fromId}-source`,
            targetHandle: `${toId}-target`
          });
        }
      });

      // Add regular service nodes
      architecture.services.forEach((service) => {
        const serviceId = createNodeId(service);
        const serviceIcon = getCachedServiceIcon(service);
        newNodes.push({
          id: serviceId,
          type: 'awsService',
          position: { x: newNodes.length * 200, y: 200 },
          data: { label: service, icon: serviceIcon, isMissing: false }
        });
      });

      setNodes(newNodes);
      setEdges(newEdges);
    }

    return () => {
      hasLoggedWarning.current = false;
    };
  }, [architecture, missingServices, setNodes, setEdges]);

  // Early return if no data
  if (!architecture?.services) {
    return (
      <div className="architecture-visualizer-loading">
        <span>Loading architecture...</span>
      </div>
    );
  }

  return (
    <div style={{ height: '500px' }} className="architecture-visualizer">
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        nodeTypes={nodeTypes}
        fitView
        minZoom={0.5}
        maxZoom={1.5}
      >
        <Background color="#4FD1C5" gap={16} size={1} />
        <Controls />
        <MiniMap
          nodeColor={(node) => node.data.isMissing ? '#FF5656' : '#4FD1C5'}
          maskColor="rgba(13, 25, 35, 0.7)"
        />
      </ReactFlow>
    </div>
  );
};

export default AWSArchitectureDisplay;
