import React, { useState, useCallback } from 'react';
import { DndContext, useSensor, useSensors, PointerSensor, TouchSensor } from '@dnd-kit/core';
import AWSArchitectureDisplay from './AWSArchitectureDisplay';
import DraggableService from './DraggableService';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import { getCachedServiceIcon, SERVICE_MAPPINGS } from '../services/IconResolver';

const S3_BUCKET_URL = 'https://hackthon-backend-files-ep-2024.s3.amazonaws.com';
const ICONS_BASE_PATH = '/amazon-icons-set/Architecture-Service-Icons_06072024';

const AWSArchitectureGame = () => {
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);
    const [architecture, setArchitecture] = useState(null);
    const [architectureInfo, setArchitectureInfo] = useState(null);
    const [currentQuestion, setCurrentQuestion] = useState(null);
    const [serviceOptions, setServiceOptions] = useState([]);
    const [difficulty] = useState('BEGINNER');
    const [score, setScore] = useState(0); 

    const API_URL = 'https://p7bwtrx47e.execute-api.us-east-1.amazonaws.com/stage/amazon-architecture';

    const sensors = useSensors(
      useSensor(PointerSensor, {
        activationConstraint: {
          distance: 8,
        },
      }),
      useSensor(TouchSensor)
    );

    const loadNextArchitecture = useCallback(async () => {
      if (isLoading) return;
      setIsLoading(true);
      setError(null);

      try {
        console.log('🚀 Fetching new architecture...');
        const response = await fetch(API_URL, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Accept': 'application/json',
            'Origin': 'http://localhost:3000'
          },
          mode: 'cors',
          credentials: 'omit',
          body: JSON.stringify({ difficulty })
        });

        if (response.status === 503) {
          throw new Error('Server is temporarily unavailable. Please try again later.');
        }

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();
        console.log('📦 Received data:', data);

        setArchitectureInfo({
          name: data.architecture.name,
          description: data.architecture.description
        });

        const transformedServices = data.services.map(serviceName => ({
          id: serviceName.toLowerCase().replace(/[^a-z0-9]+/g, '-'),
          name: serviceName,
          icon: getCachedServiceIcon(serviceName)
        }));

        const serviceNameToId = {};
        transformedServices.forEach(service => {
          serviceNameToId[service.name] = service.id;
        });

        const transformedConnections = (data.connections || []).map(conn => {
          if (!conn || !conn.from || !conn.to) {
            console.warn('⚠️ Malformed connection:', conn);
            return null;
          }

          return {
            from: serviceNameToId[conn.from] || conn.from.toLowerCase().replace(/[^a-z0-9]+/g, '-'),
            to: serviceNameToId[conn.to] || conn.to.toLowerCase().replace(/[^a-z0-9]+/g, '-')
          };
        }).filter(conn => conn !== null);

        console.log('🔗 Transformed connections:', transformedConnections);
        console.log('🔍 Service name to ID mapping:', serviceNameToId);

        setArchitecture({
          services: transformedServices,
          connections: transformedConnections
        });

        if (data.missingServices && data.missingServices.length > 0) {
          const currentMissingService = data.missingServices[0];
          console.log('❓ Current Question Data:', currentMissingService);

          setCurrentQuestion({
            text: currentMissingService.position,
            optimizationFocus: currentMissingService.optimizationFocus,
            options: currentMissingService.options.map(opt => {
              const cleanedName = opt.service
                .replace(/^(Amazon|AWS)\s+/, '')
                .replace(/\s+/g, ' ')
                .trim();

              console.log(`🔍 Processing option: ${cleanedName}`);
              const iconPath = SERVICE_MAPPINGS[cleanedName];
              const iconUrl = iconPath 
                ? `${S3_BUCKET_URL}${ICONS_BASE_PATH}/${iconPath}`
                : '/aws-icons/default.svg';

              console.log(`🎯 Icon URL for ${cleanedName}:`, iconUrl);

              return {
                id: opt.service.toLowerCase().replace(/\s+/g, '-'),
                name: opt.service,
                isCorrect: opt.isCorrect,
                isOptimal: opt.isOptimal,
                explanation: opt.explanation,
                rating: opt.rating,
                icon: iconUrl
              };
            })
          });

          setServiceOptions(currentMissingService.options);
        }
      } catch (error) {
        console.error('❌ Failed to load architecture:', error);
        setError(error.message);
      } finally {
        setIsLoading(false);
      }
    }, [API_URL, difficulty, isLoading]);

    const handleServiceSelection = async (selectedServiceId) => {
      const selectedOption = currentQuestion.options.find(opt => opt.service === selectedServiceId);

      if (selectedOption?.isCorrect) {
        const points = selectedOption.isOptimal ? 2 : 1;
        setScore(prev => prev + points);
      }
    };

    return (
      <DndProvider backend={HTML5Backend}>
        <div className="game-container">
          <div className="game-header">
            <h2>{architectureInfo?.name || 'AWS Architecture Challenge'}</h2>
            <div className="score">Score: {score}</div>
            <button 
              className="new-architecture-button"
              onClick={loadNextArchitecture}
              disabled={isLoading}
            >
              {isLoading ? 'Loading...' : 'New Architecture'}
            </button>
          </div>

          {architectureInfo && <p className="architecture-description">{architectureInfo.description}</p>}

          {error ? (
            <div className="error-message">{error}</div>
          ) : (
            <>
              <AWSArchitectureDisplay 
                architecture={architecture}
                currentQuestion={currentQuestion}
              />

              {currentQuestion && (
                <div className="question-container">
                  <h3>Question: {currentQuestion.text}</h3>
                  <p>Optimization Focus: {currentQuestion.optimizationFocus}</p>
                  <div className="service-options">
                    {currentQuestion.options?.map((option, index) => (
                      <DraggableService
                        key={`${option.id}-${index}`}
                        service={option}
                        onClick={() => handleServiceSelection(option.id)}
                      />
                    ))}
                  </div>
                </div>
              )}
            </>
          )}
        </div>
      </DndProvider>
    );
};

export default AWSArchitectureGame;
