import { SERVICE_MAPPINGS } from '../services/IconResolver';

// Use environment variables instead of hardcoded URL
const API_URL = process.env.REACT_APP_FUNCTION_URL?.replace('generate_architecture', 'service_name_and_definition') 
  || 'https://generate-architecture.azurewebsites.net/api/service_name_and_definition';
const FUNCTION_KEY = process.env.REACT_APP_AZURE_FUNCTION_KEY;

console.log('ğŸ”‘ Using function URL:', API_URL);

// Replace the hardcoded SERVICE_DEFINITIONS with a function to fetch from Azure Function
let cachedDefinitions = null;

export const getServiceDefinitions = async () => {
  console.log('ğŸ”„ Fetching service definitions...');

  // Use cached data if available to prevent unnecessary API calls
  if (cachedDefinitions) {
    console.log('ğŸ“‹ Using cached definitions:', cachedDefinitions.length, 'items');
    return cachedDefinitions;
  }

  let attempt = 0;
  const maxAttempts = 3;

  while (attempt < maxAttempts) {
    try {
      console.log(`ğŸŒ Calling Azure Function (attempt ${attempt + 1}/${maxAttempts})...`);

      const response = await fetch(API_URL, {
        method: 'GET',
        headers: { 
          'Content-Type': 'application/json',
          'x-functions-key': FUNCTION_KEY
        }
      });

      if (response.status === 503) {
        console.warn("âš ï¸ Received 503 Service Unavailable. Retrying in 3 seconds...");
        attempt++;
        await new Promise(resolve => setTimeout(resolve, 3000));
        continue;
      }

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();

      console.log('ğŸ“Š Raw Azure Function response:', data);
      console.log('ğŸ“Š Response type:', typeof data);
      console.log('ğŸ“Š Response is array:', Array.isArray(data));

      if (Array.isArray(data)) {
        console.log('ğŸ“Š First few items:', data.slice(0, 3));
      } else {
        throw new Error('Expected array response from service definitions function');
      }

      // Format the data to match your expected structure
      const formattedData = data.map(item => {
        const serviceData = {
          name: item.service,
          definition: item.description,
          path: `/azure-icons/services/${item.service.toLowerCase().replace(/\s+/g, '-')}.svg`
        };
        return serviceData;
      });

      console.log('ğŸ”„ Formatted data examples:', formattedData.slice(0, 3));
      console.log('âœ… Total service definitions:', formattedData.length);

      // Cache the data
      cachedDefinitions = formattedData;
      return formattedData;

    } catch (error) {
      console.error('âŒ Error fetching service definitions:', error);
      console.error('âŒ Error details:', error.message);

      attempt++;
      if (attempt < maxAttempts) {
        console.log(`â±ï¸ Retrying in 3 seconds... (attempt ${attempt + 1}/${maxAttempts})`);
        await new Promise(resolve => setTimeout(resolve, 3000));
      } else {
        console.log('âš ï¸ Maximum retry attempts reached, using fallback data');
        return getFallbackServiceDefinitions();
      }
    }
  }

  return getFallbackServiceDefinitions();
};

const getFallbackServiceDefinitions = () => {
  console.log('âš ï¸ Using fallback data instead');
  return [
    {
      name: "Kubernetes Services",
      definition: "Simplifies deploying, managing, and scaling containerized applications",
      path: "/azure-icons/services/kubernetes-services.svg"
    },
    {
      name: "Cognitive Services",
      definition: "Adds AI capabilities to applications through pre-built APIs",
      path: "/azure-icons/services/cognitive-services.svg"
    },
    {
      name: "Azure Functions",
      definition: "Event-driven, serverless compute service",
      path: "/azure-icons/services/azure-functions.svg"
    },
    {
      name: "Virtual Machine",
      definition: "Provides on-demand, scalable computing resources",
      path: "/azure-icons/services/virtual-machine.svg"
    },
    {
      name: "Bot Services",
      definition: "Intelligent, serverless bot service that scales on demand",
      path: "/azure-icons/services/bot-services.svg"
    }
  ];
};

// Update the getRandomServices function to work with async data
export const getRandomServices = async (count = 10) => {
  const allServices = await getServiceDefinitions();
  const shuffled = [...allServices].sort(() => 0.5 - Math.random());
  return shuffled.slice(0, count);
};

// Update other functions that use SERVICE_DEFINITIONS
export const getRandomDefinition = async () => {
  const services = await getServiceDefinitions();
  const randomIndex = Math.floor(Math.random() * services.length);
  return {
    name: services[randomIndex].name,
    definition: services[randomIndex].definition
  };
};

// Check if the game is complete (all definitions matched)
export const isGameComplete = (matchedServices, totalServices) => {
  return matchedServices.length === totalServices;
};

// Format time as MM:SS
export const formatTime = (seconds) => {
  const mins = Math.floor(seconds / 60);
  const secs = seconds % 60;
  return `${mins < 10 ? '0' : ''}${mins}:${secs < 10 ? '0' : ''}${secs}`;
};
