let openai;
try {
  const OpenAI = require('openai');
  openai = new OpenAI({
    apiKey: process.env.REACT_APP_OPENAI_API_KEY,
    dangerouslyAllowBrowser: true
  });
} catch (error) {
  console.warn('OpenAI module not loaded:', error);
  openai = null;
}

const DIFFICULTY_LEVELS = {
  BEGINNER: {
    complexity: "simple",
    services: "basic AWS services",
    temperature: 0.7
  },
  INTERMEDIATE: {
    complexity: "moderate",
    services: "intermediate AWS services",
    temperature: 0.8
  },
  ADVANCED: {
    complexity: "complex",
    services: "advanced AWS services",
    temperature: 0.9
  }
};

const FALLBACK_ARCHITECTURE = {
  architecture: {
    services: [
      {
        id: 'vpc',
        name: 'VPC',
        icon: 'https://raw.githubusercontent.com/awsicons/SVGs/master/Architecture-Service-Icons/Arch_Networking-Content-Delivery/48/Arch_Amazon-Virtual-Private-Cloud_48.svg'
      },
      {
        id: 'ec2',
        name: 'EC2',
        icon: 'https://raw.githubusercontent.com/awsicons/SVGs/master/Architecture-Service-Icons/Arch_Compute/48/Arch_Amazon-EC2_48.svg'
      },
      {
        id: 'rds',
        name: 'RDS',
        icon: 'https://raw.githubusercontent.com/awsicons/SVGs/master/Architecture-Service-Icons/Arch_Database/48/Arch_Amazon-RDS_48.svg'
      }
    ],
    connections: [
      { from: 'vpc', to: 'ec2' },
      { from: 'ec2', to: 'rds' }
    ]
  },
  question: {
    text: "Which compute service would you use for this web application?",
    position: 'compute-layer',
    missingServices: ['ec2']
  },
  options: [
    { id: 'ec2', name: 'EC2' },
    { id: 'lambda', name: 'Lambda' },
    { id: 'ecs', name: 'ECS' },
    { id: 'fargate', name: 'Fargate' }
  ]
};

const generateArchitecture = async (difficulty) => {
  return {
    architecture: {
      services: [
        { id: 'lambda', name: 'AWS Lambda', icon: '/aws-icons/Arch_AWS-Lambda_48.svg' },
        { id: 'ec2', name: 'Amazon EC2', icon: '/aws-icons/ec2.png' },
        { id: 's3', name: 'Amazon S3', icon: '/aws-icons/s3.png' }
      ],
      connections: [
        { from: 'lambda', to: 's3' },
        { from: 'ec2', to: 's3' }
      ]
    },
    question: {
      text: 'Which compute service would you use for serverless architecture?',
      missingServices: ['lambda']
    },
    options: [
      { id: 'lambda', name: 'AWS Lambda', icon: '/aws-icons/Arch_AWS-Lambda_48.svg' },
      { id: 'ec2', name: 'Amazon EC2', icon: '/aws-icons/ec2.png' },
      { id: 'ecs', name: 'Amazon ECS', icon: '/aws-icons/ecs.png' },
      { id: 'eks', name: 'Amazon EKS', icon: '/aws-icons/eks.png' }
    ]
  };
};

export { generateArchitecture };

export const validateAnswer = async (architecture, selectedService, position) => {
  try {
    const completion = await openai.chat.completions.create({
      model: "gpt-4",
      messages: [
        {
          role: "system",
          content: "You are validating AWS architecture design decisions."
        },
        {
          role: "user",
          content: `Validate if ${selectedService} is appropriate for position ${position} in the given architecture.
          Architecture context: ${JSON.stringify(architecture)}`
        }
      ],
      functions: [{
        name: "validateArchitectureChoice",
        description: "Validate the selected AWS service for the architecture",
        parameters: {
          type: "object",
          properties: {
            correct: {
              type: "boolean",
              description: "Whether the service choice is correct"
            },
            explanation: {
              type: "string",
              description: "Detailed explanation of why the choice is correct or incorrect"
            },
            bestPractices: {
              type: "array",
              items: { type: "string" },
              description: "Relevant AWS best practices"
            }
          },
          required: ["correct", "explanation"]
        }
      }],
      function_call: { name: "validateArchitectureChoice" }
    });

    const functionCall = completion.choices[0].message.function_call;
    return JSON.parse(functionCall.arguments);
  } catch (error) {
    console.error('Error validating answer:', error);
    return {
      correct: selectedService === architecture.question.missingServices[0],
      explanation: "This is a fallback explanation for the selected service."
    };
  }
};
