import React, { useState, useEffect, useCallback } from 'react';
import '../styles/GameCanvas.css';
import { getAzureIcon } from '../utils/iconResolver';

// Add the SERVICE_MAPPINGS for icon selection
const SERVICE_MAPPINGS = {
  "Virtual Machine": "virtual-machine",
  "Storage Account": "storage-account",
  "App Service": "app-service",
  "Cosmos DB": "cosmos-db",
  "SQL Database": "sql-database",
  "Function App": "function-app",
  "Logic App": "logic-app",
  "API Management": "api-management",
  "Event Hub": "event-hub",
  "Service Bus": "service-bus",
  "Key Vault": "key-vault",
  "Container Registry": "container-registry",
  "Azure Kubernetes Service": "kubernetes-service",
  "Virtual Network": "virtual-network",
  "Application Gateway": "application-gateway",
  "Azure Active Directory": "active-directory",
  "Azure Monitor": "monitor",
  "Azure DevOps": "devops",
  "Azure Cognitive Services": "cognitive-services",
  "Azure Machine Learning": "machine-learning",
  "Azure IoT Hub": "iot-hub",
  "Azure Data Factory": "data-factory",
  "Azure Synapse Analytics": "synapse-analytics",
  "Azure Databricks": "databricks"
};

const IconsMemoryGame = () => {
  const [cards, setCards] = useState([]);
  const [flippedCards, setFlippedCards] = useState([]);
  const [matchedCards, setMatchedCards] = useState([]);
  const [lockBoard, setLockBoard] = useState(false);
  const [gameOver, setGameOver] = useState(false);
  const [timeLeft, setTimeLeft] = useState(120); // 2 minutes in seconds
  const [gameStarted, setGameStarted] = useState(false);

  // Get a random selection of icons from SERVICE_MAPPINGS
  const getRandomIcons = useCallback(() => {
    const serviceNames = Object.values(SERVICE_MAPPINGS);
    const shuffled = [...serviceNames].sort(() => 0.5 - Math.random());
    return shuffled.slice(0, 12); // Get 12 random icons for 24 cards (12 pairs)
  }, []);

  // List of Azure service icons to use in the game
  const icons = getRandomIcons();

  // Initialize the game
  const initGame = useCallback(() => {
    // Create pairs of cards
    const cardPairs = [...icons, ...icons];
    // Shuffle the cards
    const shuffledCards = cardPairs.sort(() => Math.random() - 0.5);

    // Create card objects with IDs
    const cardObjects = shuffledCards.map((icon, index) => ({
      id: index,
      icon: icon,
      isFlipped: false,
      isMatched: false
    }));

    setCards(cardObjects);
    setFlippedCards([]);
    setMatchedCards([]);
    setLockBoard(false);
    setGameOver(false);
    setTimeLeft(120);
    setGameStarted(true);
  }, [icons]);

  // Handle card click
  const handleCardClick = (cardId) => {
    if (lockBoard) return;
    if (flippedCards.length === 2) return;

    // Find the clicked card
    const clickedCard = cards.find(card => card.id === cardId);

    // Don't allow clicking already flipped or matched cards
    if (clickedCard.isFlipped || clickedCard.isMatched) return;

    // Update the cards state to flip the clicked card
    setCards(prevCards => 
      prevCards.map(card => 
        card.id === cardId ? { ...card, isFlipped: true } : card
      )
    );

    // Add the clicked card to flippedCards
    setFlippedCards(prev => [...prev, clickedCard]);
  };

  // Check for matches when two cards are flipped
  useEffect(() => {
    if (flippedCards.length === 2) {
      setLockBoard(true);

      const [firstCard, secondCard] = flippedCards;

      if (firstCard.icon === secondCard.icon) {
        // It's a match!
        setMatchedCards(prev => [...prev, firstCard.icon]);

        // Mark the cards as matched
        setCards(prevCards => 
          prevCards.map(card => 
            card.id === firstCard.id || card.id === secondCard.id
              ? { ...card, isMatched: true }
              : card
          )
        );

        // Reset flipped cards
        setFlippedCards([]);
        setLockBoard(false);
      } else {
        // Not a match, flip the cards back after a delay
        setTimeout(() => {
          setCards(prevCards => 
            prevCards.map(card => 
              card.id === firstCard.id || card.id === secondCard.id
                ? { ...card, isFlipped: false }
                : card
            )
          );

          setFlippedCards([]);
          setLockBoard(false);
        }, 1500);
      }
    }
  }, [flippedCards]);

  // Check if game is over (all matches found)
  useEffect(() => {
    if (gameStarted && matchedCards.length === icons.length) {
      setGameOver(true);
    }
  }, [matchedCards, icons.length, gameStarted]);

  // Timer countdown
  useEffect(() => {
    if (!gameStarted || gameOver) return;

    const timer = setInterval(() => {
      setTimeLeft(prev => {
        if (prev <= 1) {
          clearInterval(timer);
          setGameOver(true);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, [gameStarted, gameOver]);

  // Format time as MM:SS
  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  // Start the game when component mounts
  useEffect(() => {
    initGame();
  }, [initGame]);

  // Create a 5x5 grid with cards and reset button
  const renderGrid = () => {
    const grid = [];
    const totalCells = 25; // 5x5 grid

    for (let i = 0; i < totalCells; i++) {
      if (i === 12) { // Middle position (3rd row, 3rd column)
        grid.push(
          <div 
            key="reset" 
            className="memory-game-reset"
            onClick={initGame}
          >
            Reset
          </div>
        );
      } else {
        const cardIndex = i > 12 ? i - 1 : i; // Adjust index for cards after reset button
        if (cardIndex < cards.length) {
          const card = cards[cardIndex];
          grid.push(
            <div
              key={card.id}
              className={`memory-game-card ${card.isFlipped || card.isMatched ? 'flipped' : ''}`}
              onClick={() => handleCardClick(card.id)}
            >
              {card.isFlipped || card.isMatched ? (
                <div className="memory-card-content">
                  <img src={getAzureIcon(card.icon)} alt={card.icon} />
                  <div className="memory-card-text">{card.icon}</div>
                </div>
              ) : (
                <span>?</span>
              )}
            </div>
          );
        } else {
          // Add empty cells to complete the grid if needed
          grid.push(
            <div key={`empty-${i}`} className="memory-game-card empty"></div>
          );
        }
      }
    }

    return grid;
  };

  return (
    <div className="gameCanvas-container">
      <h1 className="gameCanvas-game-title">Azure Icons Memory</h1>

      <div className="gameCanvas-game-content">
        <div className="gameCanvas-canvas-wrapper" style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}>
          <div className="memory-game-timer">TIMER: {formatTime(timeLeft)}</div>

          <div className="memory-game-grid">
            {renderGrid()}
          </div>

          {gameOver && (
            <div className="gameCanvas-game-over">
              <h3>{matchedCards.length === icons.length ? 'Congratulations!' : 'Time\'s Up!'}</h3>
              <p>
                {matchedCards.length === icons.length 
                  ? `You found all matches with ${formatTime(timeLeft)} remaining!` 
                  : `You found ${matchedCards.length} out of ${icons.length} matches.`}
              </p>
              <button onClick={initGame}>Play Again</button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default IconsMemoryGame;
