import React from 'react';
import { useDraggable } from '@dnd-kit/core';
import { CSS } from '@dnd-kit/utilities';

const ServiceOption = ({ service, onSelect }) => {
  const { attributes, listeners, setNodeRef, transform, isDragging } = useDraggable({
    id: service.id,
    data: service
  });

  const style = {
    transform: CSS.Transform.toString(transform),
    opacity: isDragging ? 0.5 : 1
  };

  return (
    <div
      ref={setNodeRef}
      {...listeners}
      {...attributes}
      className={`service-option ${isDragging ? 'dragging' : ''}`}
      onClick={() => onSelect(service.id)}
      style={style}
    >
      <img src={service.icon} alt={service.name} className="service-icon" />
      <span className="service-name">{service.name}</span>
    </div>
  );
};

const ServiceOptionsPanel = ({ options, onSelect, isLoading }) => {
  if (isLoading) {
    return <div className="options-container loading">Loading options...</div>;
  }

  return (
    <div className="options-container">
      {options.map((service) => (
        <ServiceOption 
          key={service.id}
          service={service}
          onSelect={onSelect}
        />
      ))}
    </div>
  );
};

export default ServiceOptionsPanel;
