import React, { useEffect, useRef, useState } from 'react';
import '../styles/GameCanvas.css';
import { AWS_ICON_CATEGORIES, SERVICE_MAPPINGS } from '../services/IconResolver';

// Add these constants from IconResolver.js
const S3_BUCKET_URL = 'https://hackthon-backend-files-ep-2024.s3.us-east-1.amazonaws.com';
const ICONS_BASE_PATH = '/amazon-icons-set/Architecture-Service-Icons_06072024';

// Add these constants at the top of your file
const S3_ROCKET_PATH = `${S3_BUCKET_URL}/games/rocket`;

const GameCanvas = () => {
  // First, declare all state
  const [timeLeft, setTimeLeft] = useState(60);
  const [score, setScore] = useState(0);
  const [currentInstructionIndex, setCurrentInstructionIndex] = useState(0);
  const [selectedRocket, setSelectedRocket] = useState(() => {
    const savedRocket = localStorage.getItem('selectedRocket');
    return savedRocket || 'rocket1.png';
  });
  const [showNameModal, setShowNameModal] = useState(true);
  const [tempPlayerName, setTempPlayerName] = useState('');
  const [nameError, setNameError] = useState('');
  const [playerName, setPlayerName] = useState(() => {
    const savedName = localStorage.getItem('playerName');
    return savedName || '';
  });
  const [rocketPosition, setRocketPosition] = useState({ x: 375, y: 550 });

  // Get random categories for instructions only once at game start
  const [instructions] = useState(() => {
    const getRandomCategories = () => {
      const categories = Object.keys(AWS_ICON_CATEGORIES);
      const shuffled = [...categories].sort(() => 0.5 - Math.random());
      return shuffled.slice(0, 2);
    };

    return getRandomCategories().map(category => ({
      text: `Shoot the ${category.split('_')
        .map(word => word.charAt(0).toUpperCase() + word.slice(1).toLowerCase())
        .join(' ')} icons!`,
      category: AWS_ICON_CATEGORIES[category]
    }));
  });

  // Generate iconTypes based on the selected categories
  const [iconTypes] = useState(() => {
    return instructions.reduce((acc, instruction) => {
      const categoryServices = Object.entries(SERVICE_MAPPINGS)
        .filter(([_, path]) => path.includes(instruction.category))
        .map(([name, path]) => ({
          path: `${S3_BUCKET_URL}${ICONS_BASE_PATH}/${path}`,
          name: name
        }));

      acc[instruction.category] = categoryServices;
      return acc;
    }, {});
  });

  // Then declare refs and other setup code
  const canvasRef = useRef(null);

  // We'll keep arrays for icons, bullets, and feedback in refs to avoid
  // re-rendering the whole component every time they update.
  const iconsRef = useRef([]);
  const bulletsRef = useRef([]);
  const feedbacksRef = useRef([]);

  // Add state to track image loading status
  const [imagesLoaded, setImagesLoaded] = useState(false);
  const rocketImage = useRef(new Image());
  const fireballImage = useRef(new Image());

  // Initialize images with proper loading handlers
  useEffect(() => {
    let loadedImages = 0;
    const totalImages = 2; // rocket and fireball

    const handleImageLoad = () => {
      loadedImages++;
      console.log('Image loaded:', loadedImages);
      if (loadedImages === totalImages) {
        setImagesLoaded(true);
        console.log('All images loaded');
      }
    };

    const handleImageError = (e) => {
      console.error('Error loading image:', e.target.src);
    };

    // Load rocket image
    const savedRocket = localStorage.getItem('selectedRocket') || 'rocket1.png';
    rocketImage.current = new Image();
    rocketImage.current.onload = handleImageLoad;
    rocketImage.current.onerror = handleImageError;
    rocketImage.current.src = savedRocket;
    console.log('Initial rocket image loading from:', savedRocket);

    // Load fireball image
    fireballImage.current = new Image();
    fireballImage.current.onload = handleImageLoad;
    fireballImage.current.onerror = handleImageError;
    fireballImage.current.src = `${S3_BUCKET_URL}/games/fireball.png`;

    return () => {
      rocketImage.current.onload = null;
      rocketImage.current.onerror = null;
      fireballImage.current.onload = null;
      fireballImage.current.onerror = null;
    };
  }, []);

  // Gun info
  const gunRef = useRef({
    x: 400 - 25, // center of an 800px width minus half the gun width
    y: 800 - 100, // Adjust this value to move the rocket closer to the bottom
    width: 120,
    height: 120,
  });

  // Function to get random X
  const getRandomX = () => {
    const canvas = canvasRef.current;
    return Math.floor(Math.random() * (canvas.width - 50));
  };

  // Modify loadIcons to handle image loading properly
  const loadIcons = () => {
    iconsRef.current = [];
    const positions = [];
    const currentCategory = instructions[currentInstructionIndex].category;

    // Get all icons for current category
    const categoryIcons = iconTypes[currentCategory];
    const otherCategoryIcons = Object.entries(SERVICE_MAPPINGS)
      .filter(([_, path]) => !path.includes(currentCategory))
      .map(([name, path]) => ({
        path: `${S3_BUCKET_URL}${ICONS_BASE_PATH}/${path}`,
        name: name
      }));

    const selectedCategoryIcons = categoryIcons.length <= 4 
      ? [...categoryIcons]
      : shuffleAndSlice(categoryIcons, 4);

    const selectedOtherIcons = shuffleAndSlice(otherCategoryIcons, 6);

    const createIcon = (iconInfo, x, type) => {
      return new Promise((resolve) => {
        const iconImage = new Image();
        iconImage.onload = () => {
          iconsRef.current.push({
            x,
            y: -50,
            type,
            name: iconInfo.name,
            image: iconImage,
            hit: false,
          });
          resolve();
        };
        iconImage.onerror = () => {
          console.error('Failed to load icon:', iconInfo.path);
          resolve();
        };
        iconImage.src = iconInfo.path;
      });
    };

    const loadAllIcons = async () => {
      const promises = [];

      // Load category icons
      selectedCategoryIcons.forEach((iconInfo) => {
        let x = getRandomX();
        while (positions.some((pos) => Math.abs(pos - x) < 50)) {
          x = getRandomX();
        }
        positions.push(x);
        promises.push(createIcon(iconInfo, x, currentCategory));
      });

      // Load other icons
      selectedOtherIcons.forEach((iconInfo) => {
        let x = getRandomX();
        while (positions.some((pos) => Math.abs(pos - x) < 50)) {
          x = getRandomX();
        }
        positions.push(x);
        promises.push(createIcon(iconInfo, x, 'other'));
      });

      await Promise.all(promises);
    };

    loadAllIcons();
  };

  // Helper function to shuffle array and get n unique items
  const shuffleAndSlice = (array, n) => {
    const shuffled = [...array].sort(() => 0.5 - Math.random());
    return shuffled.slice(0, Math.min(n, shuffled.length));
  };

  // Show feedback (checkmark or X) and points after a shot
  const showFeedback = (x, y, isCorrect, points, iconName) => {
    feedbacksRef.current.push({
      x,
      y,
      isCorrect,
      points,
      name: iconName,
      time: Date.now(),
    });
  };

  // Check collisions and update score
  const checkCollisions = () => {
    const bullets = bulletsRef.current;
    const icons = iconsRef.current;
    const canvas = canvasRef.current;
    const currentCategory = instructions[currentInstructionIndex].category;

    icons.forEach((icon, iconIndex) => {
      bulletsRef.current = bulletsRef.current.filter((bullet) => {
        if (
          bullet.x > icon.x &&
          bullet.x < icon.x + 50 &&
          bullet.y > icon.y &&
          bullet.y < icon.y + 50
        ) {
          icon.hit = true;

          if (icon.type === currentCategory) {
            const heightPercentage = 1 - (icon.y / canvas.height);
            const heightBonus = Math.floor(heightPercentage * 20);
            const points = 5 + heightBonus;

            setScore((prev) => {
              if (prev + points > Math.max(...highScores.map(s => s.score))) {
                showCheerleaderMessage('highScore');
              } else if (heightBonus > 15) {
                showCheerleaderMessage('goodShot');
              }
              return prev + points;
            });
            showFeedback(icon.x, icon.y, true, points, icon.name);
            playSound('correct');
            createExplosion(icon.x + 25, icon.y + 25, '#4CAF50');
          } else {
            setScore((prev) => prev - 5);
            showFeedback(icon.x, icon.y, false, -5, icon.name);
            playSound('incorrect');
          }

          icons.splice(iconIndex, 1);
          return false;
        }
        return true;
      });
    });

    if (iconsRef.current.length === 0 && timeLeft > 0) {
      playSound('levelup');
      setCurrentInstructionIndex((prev) => (prev + 1) % instructions.length);
      loadIcons();
    }
  };

  // The main game loop, which draws everything on the canvas
  const gameLoop = () => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    ctx.clearRect(0, 0, canvas.width, canvas.height);

    if (imagesLoaded) {
      // Draw rocket
      if (rocketImage.current.complete && rocketImage.current.naturalWidth !== 0) {
        const { x: gunX, y: gunY, width, height } = gunRef.current;
        try {
          ctx.drawImage(rocketImage.current, gunX, gunY, width, height);
        } catch (error) {
          console.error('Error drawing rocket:', error);
        }
      } else {
        console.log('Rocket image not ready:', rocketImage.current.src);
      }

      // Draw icons
      iconsRef.current.forEach((icon) => {
        if (!icon.hit && icon.image.complete && icon.image.naturalWidth !== 0) {
          ctx.save();

          const radius = 10;
          const x = icon.x;
          const y = icon.y;
          const width = 50;
          const height = 50;

          ctx.beginPath();
          ctx.moveTo(x + radius, y);
          ctx.lineTo(x + width - radius, y);
          ctx.quadraticCurveTo(x + width, y, x + width, y + radius);
          ctx.lineTo(x + width, y + height - radius);
          ctx.quadraticCurveTo(x + width, y + height, x + width - radius, y + height);
          ctx.lineTo(x + radius, y + height);
          ctx.quadraticCurveTo(x, y + height, x, y + height - radius);
          ctx.lineTo(x, y + radius);
          ctx.quadraticCurveTo(x, y, x + radius, y);
          ctx.closePath();

          ctx.clip();

          ctx.drawImage(icon.image, x, y, width, height);

          ctx.restore();

          icon.y += 0.3;
        }
      });


      // Remove icons that are off screen and check if we need new ones
      iconsRef.current = iconsRef.current.filter(icon => icon.y < canvas.height);
      if (iconsRef.current.length === 0 && timeLeft > 0) {
        playSound('levelup');
        setCurrentInstructionIndex((prev) => (prev + 1) % instructions.length);
        loadIcons();
      }

      // Draw bullets only if fireball image is loaded
      if (fireballImage.current.complete && !fireballImage.current.naturalWidth === 0) {
        bulletsRef.current.forEach((bullet) => {
          ctx.drawImage(fireballImage.current, bullet.x, bullet.y, 20, 20);
          bullet.y -= 5;
        });
      }

      // Remove bullets that are off-screen
      bulletsRef.current = bulletsRef.current.filter((b) => b.y > 0);

      // Draw feedback with consistent styling for both positive and negative points
      const now = Date.now();
      feedbacksRef.current = feedbacksRef.current.filter((feedback) => {
        const elapsed = now - feedback.time;
        if (elapsed < 3000) {
          // Draw points with consistent styling
          ctx.shadowColor = 'rgba(0, 0, 0, 0.5)';
          ctx.shadowBlur = 4;
          ctx.fillStyle = feedback.isCorrect ? '#4CAF50' : '#F44336';
          ctx.font = 'bold 28px Arial';

          // Show points with + or - sign
          const pointsText = feedback.points > 0 ? `+${feedback.points}` : `${feedback.points}`;
          ctx.fillText(
            pointsText,
            feedback.x + 10,
            feedback.y - 20
          );

          // Draw icon name
          ctx.font = '16px Arial';
          ctx.fillText(
            feedback.name,
            feedback.x + 60,
            feedback.y + 25
          );

          // Draw feedback symbol
          ctx.font = 'bold 55px Arial';
          ctx.fillText(
            feedback.isCorrect ? 'âœ”' : 'âœ˜',
            feedback.x + 10,
            feedback.y + 30
          );

          ctx.shadowBlur = 0;
          return true;
        }
        return false;
      });

      checkCollisions();

      // Update and draw particles
      particlesRef.current = particlesRef.current.filter(particle => {
        if (particle.lifetime > 0 && particle.size > 0) {
          particle.update();
          particle.draw(ctx);
          return true;
        }
        return false;
      });
    }

    requestAnimationFrame(gameLoop);
  };

  // Add sound references with error handling
  const sounds = useRef({});
  const [soundsLoaded, setSoundsLoaded] = useState(false);

  // Initialize sounds with error handling
  useEffect(() => {
    const soundFiles = {
      shoot:  `${S3_BUCKET_URL}/games/sounds/pistol_sound.mp3`,
      correct: `${S3_BUCKET_URL}/games/sounds/correct_sound.mp3`,
      incorrect:`${S3_BUCKET_URL}/games/sounds/incorrect_sound.mp3`,
      levelup: `${S3_BUCKET_URL}/games/sounds/levelup.mp3`
    };

    // Create a safe audio player that won't break if files are missing
    const createSafeAudio = (path) => {
      const audio = new Audio();

      return new Promise((resolve) => {
        audio.addEventListener('error', () => {
          console.warn(`Sound file not found: ${path}`);
          resolve(null);
        });

        audio.addEventListener('canplaythrough', () => {
          resolve(audio);
        });

        // Set timeout in case the file takes too long to load
        setTimeout(() => resolve(null), 2000);

        audio.src = path;
      });
    };

    // Load all sound files with error handling
    const loadSounds = async () => {
      const loadedSounds = {};

      for (const [key, path] of Object.entries(soundFiles)) {
        loadedSounds[key] = await createSafeAudio(path);
      }

      sounds.current = loadedSounds;
      setSoundsLoaded(true);
    };

    loadSounds();

    // Set initial volume
    return () => {
      // Cleanup sounds on unmount
      Object.values(sounds.current).forEach(sound => {
        if (sound) {
          sound.pause();
          sound.src = '';
        }
      });
    };
  }, []);

  // Safe play function
  const playSound = (soundName) => {
    const sound = sounds.current[soundName];
    if (sound && !isMuted) {
      try {
        sound.currentTime = 0;
        sound.volume = 0.3;
        sound.play().catch(err => {
          console.warn(`Error playing sound ${soundName}:`, err);
        });
      } catch (err) {
        console.warn(`Error with sound ${soundName}:`, err);
      }
    }
  };

  // Add a mute toggle button to the UI
  const [isMuted, setIsMuted] = useState(false);

  const toggleMute = () => {
    setIsMuted(!isMuted);
    Object.values(sounds.current).forEach(sound => {
      if (sound) {
        sound.muted = !isMuted;
      }
    });
  };

  // Add new state and refs for particles and timer
  const [highScores, setHighScores] = useState(() => {
    const saved = localStorage.getItem('highScores');
    return saved ? JSON.parse(saved) : [];
  });
  const [gameOver, setGameOver] = useState(false);
  const particlesRef = useRef([]);

  // Add state to track if we've already handled game over
  const [gameOverHandled, setGameOverHandled] = useState(false);

  // Add state to track if game should start
  const [gameStarted, setGameStarted] = useState(false);

  // Particle class
  class Particle {
    constructor(x, y, color) {
      this.x = x;
      this.y = y;
      this.color = color;
      this.size = Math.random() * 3 + 2;
      this.speedX = Math.random() * 6 - 3;
      this.speedY = Math.random() * 6 - 3;
      this.lifetime = 1;
      this.initialSize = this.size; // Store initial size
    }

    update() {
      this.x += this.speedX;
      this.y += this.speedY;
      this.lifetime -= 0.02;
      // Calculate size based on lifetime instead of continuous subtraction
      this.size = Math.max(0, this.initialSize * this.lifetime);
    }

    draw(ctx) {
      if (this.size <= 0) return; // Don't draw if size is 0 or negative

      ctx.fillStyle = this.color;
      ctx.globalAlpha = this.lifetime;
      ctx.beginPath();
      ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
      ctx.fill();
      ctx.globalAlpha = 1;
    }
  }

  // Function to create explosion effect
  const createExplosion = (x, y, color) => {
    for (let i = 0; i < 20; i++) {
      particlesRef.current.push(new Particle(x, y, color));
    }
  };

  // Timer effect
  useEffect(() => {
    if (gameStarted && !gameOver && timeLeft > 0) {
      const timer = setInterval(() => {
        setTimeLeft(prev => prev - 1);
      }, 1000);

      return () => clearInterval(timer);
    } else if (timeLeft === 0 && !gameOverHandled) {
      handleGameOver();
    }
  }, [timeLeft, gameOver, gameOverHandled, gameStarted]);

  // Handle game over
  const handleGameOver = () => {
    if (gameOverHandled) return;

    setGameOver(true);
    setGameOverHandled(true);

    const newScore = { name: playerName, score };
    const newHighScores = [...highScores, newScore]
      .sort((a, b) => b.score - a.score)
      .slice(0, 5);

    setHighScores(newHighScores);
    localStorage.setItem('highScores', JSON.stringify(newHighScores));
  };

  // Modify the canvas setup effect
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return; // Guard clause for when canvas isn't ready

    canvas.width = 1200;
    canvas.height = 800;

    const ctx = canvas.getContext('2d');
    ctx.imageSmoothingEnabled = true;

    // Only start the game loop and load icons when both canvas and images are ready
    if (imagesLoaded) {
      loadIcons();
      const animationId = requestAnimationFrame(gameLoop);
      return () => cancelAnimationFrame(animationId);
    }
  }, [imagesLoaded]); // Add imagesLoaded as a dependency

  // Second effect for event listeners
  useEffect(() => {
    const canvas = canvasRef.current;

    const ROCKET_WIDTH = 50;
    const FIXED_ROCKET_Y = 550;

    const handleMouseMove = (e) => {
      const rect = canvas.getBoundingClientRect();

      // Calculate new X position
      let newX = e.clientX - rect.left - (gunRef.current.width / 2);

      // Add boundary checks
      newX = Math.max(-30, Math.min(newX, canvas.width - gunRef.current.width + 30));

      // Update gun position
      gunRef.current.x = newX;
    };

    const handleCanvasClick = () => {
      if (gameStarted && timeLeft > 0) {
        playSound('shoot');

        bulletsRef.current.push({
          x: gunRef.current.x + gunRef.current.width / 2 - 10,
          y: gunRef.current.y - 20,
        });
      }
    };

    canvas.addEventListener('mousemove', handleMouseMove);
    canvas.addEventListener('click', handleCanvasClick);

    return () => {
      canvas.removeEventListener('mousemove', handleMouseMove);
      canvas.removeEventListener('click', handleCanvasClick);
    };
  }, [gameStarted]);

  // Clear both name and rocket selection when component mounts
  useEffect(() => {
    localStorage.removeItem('playerName');
    localStorage.removeItem('selectedRocket');
  }, []);

  // Add state for modal visibility
  const [showRocketModal, setShowRocketModal] = useState(true);

  // Update the rocket options with proper S3 paths
  const rocketOptions = [
    { id: `${S3_ROCKET_PATH}/rocket1.png`, name: 'Modern Rocket' },
    { id: `${S3_ROCKET_PATH}/rocket2.png`, name: 'Power Rocket' },
    { id: `${S3_ROCKET_PATH}/rocket3.png`, name: 'Stealth Rocket' },
    { id: `${S3_ROCKET_PATH}/rocket4.png`, name: 'Classic Rocket' },
  ];

  // Update the handleRocketSelect function
  const handleRocketSelect = (rocketId) => {
    setSelectedRocket(rocketId);
    localStorage.setItem('selectedRocket', rocketId);

    // Use the full S3 URL directly
    rocketImage.current.src = rocketId; // rocketId now contains the full S3 URL

    // Add logging to debug image loading
    console.log('Loading rocket image from:', rocketId);

    rocketImage.current.onload = () => {
      console.log('Rocket image loaded successfully');
      setShowRocketModal(false);
      setGameStarted(true);
    };

    rocketImage.current.onerror = (error) => {
      console.error('Error loading rocket image:', error);
    };
  };

  // Add state for cheerleader messages
  const [cheerMessage, setCheerMessage] = useState('');
  const [showCheerMessage, setShowCheerMessage] = useState(false);

  // Add cheerleader messages for different situations
  const cheerleaderMessages = {
    goodShot: [
      "Great shot! You're an AWS Pro! ðŸŒŸ",
      "Amazing cloud skills! Keep it up! ðŸŽ¯",
      "You're on fire in the cloud! ðŸ”¥",
      "That's how AWS experts do it! ðŸ‘",
    ],
    combo: [
      "Cloud combo! ðŸŽ®",
      "You're scaling infinitely! âš¡",
      "What an AWS streak! ðŸŒˆ",
    ],
    timeWarning: [
      "30 seconds left - you're highly available! â°",
      "Time for the final deployment! ðŸ’ª",
      "Make every compute unit count! â­",
    ],
    encouragement: [
      "You've got this, cloud warrior! ðŸš€",
      "Keep going! You're AWS certified material! ðŸŒŸ",
      "I believe in your cloud powers! ðŸ’«",
      "Show them your AWS expertise! ðŸ’ª",
    ],
    highScore: [
      "New high score! You're going serverless! ðŸ†",
      "You're breaking cloud records! ðŸŽ–ï¸",
      "Legendary AWS performance! ðŸ‘‘",
    ]
  };

  // Function to show a random message from a category
  const showCheerleaderMessage = (category) => {
    const messages = cheerleaderMessages[category];
    const randomMessage = messages[Math.floor(Math.random() * messages.length)];
    setCheerMessage(randomMessage);
    setShowCheerMessage(true);

    // Hide message after 3 seconds
    setTimeout(() => {
      setShowCheerMessage(false);
    }, 3000);
  };

  // Add periodic encouragement
  useEffect(() => {
    const encouragementInterval = setInterval(() => {
      if (timeLeft > 0 && Math.random() < 0.3) {
        showCheerleaderMessage('encouragement');
      }
    }, 10000);

    return () => clearInterval(encouragementInterval);
  }, [timeLeft]);

  // Add time warning messages
  useEffect(() => {
    if (timeLeft === 30) {
      showCheerleaderMessage('timeWarning');
    }
  }, [timeLeft]);

  // Add this function near other handlers (around line 440)
  const handleNameSubmit = (e) => {
    e.preventDefault();
    if (tempPlayerName.trim().length < 2) {
      setNameError('Name must be at least 2 characters long');
      return;
    }
    if (tempPlayerName.trim().length > 15) {
      setNameError('Name must be less than 15 characters');
      return;
    }
    setPlayerName(tempPlayerName.trim());
    localStorage.setItem('playerName', tempPlayerName.trim());
    setShowNameModal(false);
  };

  return (
    <div className="gameCanvas-container">
      <h1 className="gameCanvas-game-title">AWS Cloud Shooter</h1>
      <div className="gameCanvas-game-content">
        <div className="gameCanvas-canvas-wrapper">
          <canvas ref={canvasRef} className="gameCanvas-game-canvas" />
        </div>
        <div className="gameCanvas-instruction-panel">
          <div className="gameCanvas-player-info">
            <div className="gameCanvas-player-name">
              Player: {playerName}
            </div>
            <div className="gameCanvas-score-display">
              <span role="img" aria-label="alien">ðŸ‘¾</span>{' '}
              Score: {score}{' '}
              <span role="img" aria-label="joystick">ðŸ•¹ï¸</span>

              {soundsLoaded && Object.values(sounds.current).some(sound => sound) && (
                <button 
                  className="gameCanvas-sound-toggle"
                  onClick={toggleMute}
                  aria-label="Toggle sound"
                >
                  {isMuted ? 'ðŸ”‡' : 'ðŸ”Š'}
                </button>
              )}
            </div>
          </div>

          <div className="gameCanvas-timer-display">
            Time: {timeLeft}s
          </div>

          <div className="gameCanvas-score-explanation">
            <h4>How to Score:</h4>
            <ul>
            <li>â€¢ Hit correct icon: +5 points</li>
            <li>â€¢ Height bonus: up to +20</li>
            <li>â€¢ Wrong hit: -5 points</li>
            <li>â€¢ Tip: Shoot icons as high as possible!</li>
            </ul>
          </div>

          <div className="gameCanvas-high-scores">
          <h4>High Scores</h4>
          <ul>
            {highScores.map((entry, index) => (
              <li key={index}>
                <span className="gameCanvas-high-score-name">{entry.name}:</span> {entry.score}
              </li>
            ))}
          </ul>
        </div>

          <div className="gameCanvas-mission-container">
            <h3 className="gameCanvas-mission-title">
              Current Mission
            </h3>

            <div className="gameCanvas-mission-text">
              {instructions[currentInstructionIndex].text}
            </div>
          </div>
        </div>
      </div>

      {showRocketModal && (
        <div className="gameCanvas-modal-overlay">
          <div className="gameCanvas-rocket-selection-modal">
            <h2>Choose Your Rocket</h2>
            <div className="gameCanvas-rocket-grid">
              {rocketOptions.map((rocket) => (
                <div 
                  key={rocket.id} 
                  className={`gameCanvas-rocket-option ${selectedRocket === rocket.id ? 'selected' : ''}`}
                  onClick={() => handleRocketSelect(rocket.id)}
                >
                  <img 
                    src={rocket.id} 
                    alt={rocket.name}
                  />
                  <p>{rocket.name}</p>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}

      {gameOver && (
        <div className="gameCanvas-game-over">
          <h3>Game Over!</h3>
          <button onClick={() => window.location.reload()}>
            Play Again
          </button>
        </div>
      )}

      {showCheerMessage && (
        <div className="gameCanvas-cheerleader-container">
          <div className="gameCanvas-cheerleader-character">
            <img src="icons/cheerleader.png" alt="Cheerleader" />
          </div>
          <div className="gameCanvas-cheer-message">
            {cheerMessage}
          </div>
        </div>
      )}

      {showNameModal && (
        <div className="gameCanvas-modal-overlay">
          <div className="gameCanvas-modal">
            <h2>Welcome to AWS Cloud Shooter!</h2>
            <p>Enter your name to start the game:</p>
            <form onSubmit={handleNameSubmit}>
              <input
                type="text"
                value={tempPlayerName}
                onChange={(e) => {
                  setTempPlayerName(e.target.value);
                  setNameError('');
                }}
                placeholder="Your name"
                className="gameCanvas-name-input"
                autoFocus
              />
              {nameError && <div className="gameCanvas-name-error">{nameError}</div>}
              <button type="submit" className="gameCanvas-start-button">
                Start Game
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

