import React, { useMemo, useState, useEffect, useCallback } from 'react';
import ReactFlow, { 
  Background, 
  Controls, 
  MiniMap,
  useNodesState,
  useEdgesState,
  MarkerType,
  Handle
} from 'reactflow';
import 'reactflow/dist/style.css';
import { motion } from 'framer-motion';
import { getCachedServiceIcon } from '../services/IconResolver';

// Move AWSServiceNode outside and memoize it
const AWSServiceNode = React.memo(({ data }) => (
  <motion.div
    className={`aws-service-node ${data.isMissing ? 'missing' : ''}`}
    initial={{ scale: 0, opacity: 0 }}
    animate={{ scale: 1, opacity: 1 }}
    exit={{ scale: 0, opacity: 0 }}
    whileHover={{ scale: 1.1 }}
    transition={{ type: "spring", stiffness: 300 }}
  >
    <Handle 
      type="target" 
      position="top" 
      id={`${data.id}-target`}
      className="handle"
    />
    <div className="node-content">
      {data.isMissing ? (
        <div className="missing-service-placeholder">
          <span>?</span>
        </div>
      ) : (
        <img src={data.icon} alt={data.label} className="service-icon" />
      )}
      <span className="service-label">{data.label}</span>
    </div>
    <Handle 
      type="source" 
      position="bottom" 
      id={`${data.id}-source`}
      className="handle"
    />
  </motion.div>
));

// Move nodeTypes outside component
const nodeTypes = {
  awsService: AWSServiceNode
};

const createNodeId = (serviceName) => {
  if (typeof serviceName !== 'string') {
    console.error('Invalid service name:', serviceName);
    return 'invalid-service';
  }
  return serviceName.toLowerCase().replace(/\s+/g, '-');
};

const AWSArchitectureDisplay = ({ architecture, currentQuestion }) => {
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);

  const onNodesChange = useCallback(
    (changes) => setNodes((nds) => applyNodeChanges(changes, nds)),
    []
  );

  const onEdgesChange = useCallback(
    (changes) => setEdges((eds) => applyEdgeChanges(changes, eds)),
    []
  );

  useEffect(() => {
    if (!architecture) return;

    // Create graph structure
    const graph = {
      nodes: new Map(),
      children: new Map(),
      parents: new Map(),
    };

    // Initialize graph
    architecture.services.forEach(service => {
      const id = service.id || service.name.toLowerCase().replace(/[^a-z0-9]+/g, '-');
      graph.nodes.set(id, service);
      graph.children.set(id, new Set());
      graph.parents.set(id, new Set());
    });

    // Build relationships
    architecture.connections?.forEach(conn => {
      const fromId = typeof conn.from === 'string' ? 
        conn.from.toLowerCase().replace(/[^a-z0-9]+/g, '-') : conn.from.id;
      const toId = typeof conn.to === 'string' ? 
        conn.to.toLowerCase().replace(/[^a-z0-9]+/g, '-') : conn.to.id;

      graph.children.get(fromId).add(toId);
      graph.parents.get(toId).add(fromId);
    });

    // Find root nodes (no parents)
    const rootNodes = Array.from(graph.nodes.keys())
      .filter(id => graph.parents.get(id).size === 0);

    // Layout constants
    const VERTICAL_GAP = 150;
    const HORIZONTAL_GAP = 300;
    const newNodes = [];
    const newEdges = [];

    // Calculate levels (distance from root)
    const levels = new Map();
    const calculateLevels = (nodeId, level = 0) => {
      if (!levels.has(nodeId) || levels.get(nodeId) < level) {
        levels.set(nodeId, level);
        graph.children.get(nodeId).forEach(childId => {
          calculateLevels(childId, level + 1);
        });
      }
    };
    rootNodes.forEach(rootId => calculateLevels(rootId));

    // Group nodes by level
    const nodesPerLevel = new Map();
    levels.forEach((level, nodeId) => {
      if (!nodesPerLevel.has(level)) {
        nodesPerLevel.set(level, []);
      }
      nodesPerLevel.get(level).push(nodeId);
    });

    // Position nodes
    nodesPerLevel.forEach((levelNodes, level) => {
      const levelHeight = levelNodes.length * VERTICAL_GAP;
      const startY = -(levelHeight / 2) + (VERTICAL_GAP / 2);

      levelNodes.forEach((nodeId, index) => {
        const service = graph.nodes.get(nodeId);
        const x = level * HORIZONTAL_GAP;
        const y = startY + (index * VERTICAL_GAP);

        newNodes.push({
          id: nodeId,
          type: 'awsService',
          position: { x, y },
          data: {
            label: service.name,
            icon: service.icon,
            isMissing: service.name.toLowerCase().includes('missing')
          },
          style: {
            width: 120,
            height: 80
          },
          draggable: true // Enable node dragging
        });
      });
    });

    // Create edges with improved visibility
    architecture.connections?.forEach(conn => {
      const fromId = typeof conn.from === 'string' ? 
        conn.from.toLowerCase().replace(/[^a-z0-9]+/g, '-') : conn.from.id;
      const toId = typeof conn.to === 'string' ? 
        conn.to.toLowerCase().replace(/[^a-z0-9]+/g, '-') : conn.to.id;

      newEdges.push({
        id: `${fromId}-${toId}`,
        source: fromId,
        target: toId,
        type: 'step', // Changed to step for better visibility
        animated: true,
        style: { 
          stroke: '#4FD1C5', 
          strokeWidth: 3, // Increased width
          opacity: 1 // Full opacity
        },
        markerEnd: { 
          type: MarkerType.ArrowClosed,
          width: 20,
          height: 20,
          color: '#4FD1C5'
        }
      });
    });

    setNodes(newNodes);
    setEdges(newEdges);
  }, [architecture]);

  return (
    <div style={{ height: '600px', width: '100%' }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        nodeTypes={nodeTypes}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        fitView
        fitViewOptions={{
          padding: 0.3,
          minZoom: 0.5,
          maxZoom: 1.5
        }}
        defaultViewport={{ zoom: 1 }}
        minZoom={0.5}
        maxZoom={1.5}
      >
        <Background color="#4FD1C5" gap={16} size={1} />
        <Controls />
      </ReactFlow>
    </div>
  );
};

export default AWSArchitectureDisplay;
