import React from 'react';
import { useDrop } from 'react-dnd';

const AWSArchitectureDisplay = ({ architecture, missingServices, onServiceClick }) => {
  console.log('Rendering AWSArchitectureDisplay:', { architecture, missingServices });

  const [{ isOver }, drop] = useDrop(() => ({
    accept: 'AWS_SERVICE',
    drop: (item, monitor) => {
      console.log('Drop event:', item);
      onServiceClick(item.id);
    },
    collect: (monitor) => ({
      isOver: !!monitor.isOver()
    })
  }));

  console.log('Drop state:', { isOver });

  if (!architecture || !architecture.services) {
    return (
      <div className="architecture-container">
        <div className="loading-state">Loading Architecture...</div>
      </div>
    );
  }

  const centerX = 400;
  const centerY = 250;
  const radius = 150;
  const angleStep = (2 * Math.PI) / architecture.services.length;

  return (
    <div 
      ref={drop} 
      className={`architecture-container ${isOver ? 'drop-target' : ''}`}
    >
      <svg className="connections-layer" width="100%" height="100%">
        {architecture.connections?.map((conn, idx) => {
          const fromService = architecture.services.find(s => s.id === conn.from);
          const toService = architecture.services.find(s => s.id === conn.to);
          const fromIndex = architecture.services.indexOf(fromService);
          const toIndex = architecture.services.indexOf(toService);

          const fromX = centerX + radius * Math.cos(fromIndex * angleStep);
          const fromY = centerY + radius * Math.sin(fromIndex * angleStep);
          const toX = centerX + radius * Math.cos(toIndex * angleStep);
          const toY = centerY + radius * Math.sin(toIndex * angleStep);

          return (
            <line
              key={idx}
              x1={fromX}
              y1={fromY}
              x2={toX}
              y2={toY}
              stroke="#FF9900"
              strokeWidth="2"
              strokeDasharray={conn.dashed ? "5,5" : ""}
            />
          );
        })}
      </svg>

      <div className="architecture-diagram">
        {architecture.services.map((service, index) => {
          const x = centerX + radius * Math.cos(index * angleStep);
          const y = centerY + radius * Math.sin(index * angleStep);
          const isMissing = missingServices?.includes(service.id);

          return (
            <div
              key={service.id}
              className={`service-node ${isMissing ? 'missing' : ''}`}
              style={{
                position: 'absolute',
                left: `${x}px`,
                top: `${y}px`,
                transform: 'translate(-50%, -50%)',
                cursor: isMissing ? 'pointer' : 'default'
              }}
              onClick={() => isMissing && onServiceClick(service.id)}
            >
              <img
                src={service.icon}
                alt={service.name}
                className="service-icon"
              />
              <div className="service-label">{service.name}</div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default AWSArchitectureDisplay;
