import React, { useState, useEffect } from "react";
import MagicalQuestionCard from "./MagicalQuestionCard";
import AWSArchitectureDisplay from "./AWSArchitectureDisplay";
import { motion } from "framer-motion";
import * as AWSService from "../services/AWSArchitectureAI";

const GamePresenter = () => {
  const [currentArchitecture, setCurrentArchitecture] = useState(null);
  const [currentQuestion, setCurrentQuestion] = useState(null);
  const [options, setOptions] = useState([]);
  const [triggerEffect, setTriggerEffect] = useState(0);
  const [score, setScore] = useState(0);
  const [isLoading, setIsLoading] = useState(true);

  const fetchNextArchitecture = async () => {
    setIsLoading(true);
    try {
      const data = await AWSService.generateArchitecture();
      setCurrentArchitecture(data.architecture);
      setCurrentQuestion(data.question);
      setOptions(data.options);
    } catch (error) {
      console.error('Error fetching architecture:', error);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchNextArchitecture();
  }, []);

  const handleServiceSelection = async (selectedService) => {
    if (isLoading) return;

    setTriggerEffect(prev => prev + 1);

    try {
      const result = await AWSService.validateAnswer(
        currentArchitecture,
        selectedService,
        currentQuestion.position
      );

      if (result.correct) {
        setScore(prev => prev + 1);
      }

      // Wait for animation
      setTimeout(() => {
        fetchNextArchitecture();
      }, 750);
    } catch (error) {
      console.error('Error validating answer:', error);
    }
  };

  if (isLoading) {
    return (
      <div className="loading-container">
        <h2>Loading...</h2>
      </div>
    );
  }

  return (
    <motion.div 
      className="game-presenter"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <h1>AWS Architecture Challenge</h1>

      <div className="score-display">
        Score: {score}
      </div>

      {currentArchitecture && (
        <AWSArchitectureDisplay 
          architecture={currentArchitecture}
          missingServices={currentQuestion?.missingServices || []}
          onServiceClick={handleServiceSelection}
        />
      )}

      {!isLoading && options.length > 0 && (
        <div className="options-container">
          {options.map((option) => (
            <MagicalQuestionCard 
              key={option.id}
              question={option.name}
              triggerEffect={triggerEffect}
              onClick={() => handleServiceSelection(option.id)}
            />
          ))}
        </div>
      )}
    </motion.div>
  );
};

export default GamePresenter;
