import React, { useEffect, useState } from 'react';
import ReactFlow, { 
  Background, 
  Controls, 
  MiniMap,
  useNodesState,
  useEdgesState,
  MarkerType,
  Handle
} from 'reactflow';
import 'reactflow/dist/style.css';
import { motion } from 'framer-motion';
import { getCachedServiceIcon } from '../services/IconResolver';

const AWSServiceNode = ({ data }) => (
  <motion.div
    className={`aws-service-node ${data.isMissing ? 'missing' : ''}`}
    initial={{ scale: 0, opacity: 0 }}
    animate={{ scale: 1, opacity: 1 }}
    exit={{ scale: 0, opacity: 0 }}
    whileHover={{ scale: 1.1 }}
    transition={{ type: "spring", stiffness: 300 }}
  >
    <Handle 
      type="target" 
      position="top" 
      id={`${data.id}-target`}
      className="handle"
    />
    <div className="node-content">
      {data.isMissing ? (
        <div className="missing-service-placeholder">
          <span>?</span>
        </div>
      ) : (
        <img src={data.icon} alt={data.label} className="service-icon" />
      )}
      <span className="service-label">{data.label}</span>
    </div>
    <Handle 
      type="source" 
      position="bottom" 
      id={`${data.id}-source`}
      className="handle"
    />
  </motion.div>
);

const nodeTypes = {
  awsService: AWSServiceNode
};

const createNodeId = (serviceName) => {
  if (typeof serviceName !== 'string') {
    console.error('Invalid service name:', serviceName);
    return 'invalid-service';
  }
  return serviceName.toLowerCase().replace(/\s+/g, '-');
};

const AWSArchitectureDisplay = ({ architecture, missingServices, currentQuestion }) => {
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);

  // Add a ref to track if we've logged the warning
  const hasLoggedWarning = React.useRef(false);

  useEffect(() => {
    if (architecture) {
      // Create nodes for all services
      const newNodes = architecture.services.map((service, index) => ({
        id: service.id.toLowerCase().replace(/[^a-z0-9]+/g, '-'),
        type: 'default',
        data: { 
          label: (
            <div>
              <img src={service.icon} alt={service.name} style={{ width: 30, height: 30 }} />
              <div>{service.name}</div>
            </div>
          )
        },
        position: { x: index * 200, y: Math.floor(index / 3) * 200 },
        style: service.name.includes('missing') ? { background: '#ffcccc' } : {}
      }));

      // Create edges for all connections
      const newEdges = architecture.connections.map((connection, index) => ({
        id: `e${index}`,
        source: connection.from.toLowerCase().replace(/[^a-z0-9]+/g, '-'),
        target: connection.to.toLowerCase().replace(/[^a-z0-9]+/g, '-'),
        type: 'smoothstep',
      }));

      setNodes(newNodes);
      setEdges(newEdges);
    }
  }, [architecture]);

  React.useEffect(() => {
    // Only log once per mount
    if (!architecture?.services && !hasLoggedWarning.current) {
      hasLoggedWarning.current = true;
      console.log('âš ï¸ Waiting for architecture data...');
      return;
    }

    if (architecture?.services) {
      console.log('ðŸ“Š Rendering architecture:', {
        servicesCount: architecture.services.length,
        connectionsCount: architecture.connections?.length
      });

      const newNodes = [];
      const newEdges = [];

      // Process connections and insert missing services
      (architecture.connections || []).forEach((conn) => {
        const fromId = typeof conn.from === 'string' ? createNodeId(conn.from) : conn.from.id;
        const toId = typeof conn.to === 'string' ? createNodeId(conn.to) : conn.to.id;

        // Check if there's a missing service between these nodes
        const missingService = missingServices?.find(ms => 
          ms.position.includes(fromId) && ms.position.includes(toId)
        );

        if (missingService) {
          const placeholderId = `missing-${fromId}-${toId}`;
          newNodes.push({
            id: placeholderId,
            type: 'awsService',
            position: { 
              x: newNodes.length * 200,
              y: 200 
            },
            data: {
              label: 'Missing Service',
              isMissing: true
            }
          });

          newEdges.push({
            id: `${fromId}-${placeholderId}`,
            source: fromId,
            target: placeholderId,
            type: 'smoothstep',
            animated: true,
            style: { stroke: '#4FD1C5', strokeWidth: 2 },
            markerEnd: { type: MarkerType.ArrowClosed }
          });
          newEdges.push({
            id: `${placeholderId}-${toId}`,
            source: placeholderId,
            target: toId,
            type: 'smoothstep',
            animated: true,
            style: { stroke: '#4FD1C5', strokeWidth: 2 },
            markerEnd: { type: MarkerType.ArrowClosed }
          });
        } else {
          newEdges.push({
            id: `${fromId}-${toId}`,
            source: fromId,
            target: toId,
            sourceHandle: `${fromId}-source`,
            targetHandle: `${toId}-target`,
            type: 'smoothstep',
            animated: true,
            style: { stroke: '#4FD1C5', strokeWidth: 2 },
            markerEnd: { type: MarkerType.ArrowClosed }
          });
        }
      });

      // Add regular service nodes
      architecture.services.forEach((service) => {
        const serviceId = typeof service === 'string' ? createNodeId(service) : service.id;
        const serviceName = typeof service === 'string' ? service : service.name;
        const serviceIcon = typeof service === 'string' ? getCachedServiceIcon(service) : service.icon;

        newNodes.push({
          id: serviceId,
          type: 'awsService',
          position: { x: newNodes.length * 200, y: 200 },
          data: {
            label: serviceName,
            icon: serviceIcon,
            isMissing: false
          }
        });
      });

      setNodes(newNodes);
      setEdges(newEdges);
    }

    // Cleanup function
    return () => {
      hasLoggedWarning.current = false;
    };
  }, [architecture, missingServices, setNodes, setEdges]);

  // Early return if no data
  if (!architecture?.services) {
    return (
      <div className="architecture-visualizer-loading">
        <span>Loading architecture...</span>
      </div>
    );
  }

  return (
    <div style={{ width: '100%', height: '500px' }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        nodeTypes={nodeTypes}
        fitView
        minZoom={0.5}
        maxZoom={1.5}
      >
        <Background color="#4FD1C5" gap={16} size={1} />
        <Controls />
        <MiniMap 
          nodeColor={(node) => node.data.isMissing ? '#FF5656' : '#4FD1C5'}
          maskColor="rgba(13, 25, 35, 0.7)"
        />
      </ReactFlow>
    </div>
  );
};

export default AWSArchitectureDisplay;
