                  // Find the category
                  for (const [category, path] of Object.entries(AZURE_ICON_CATEGORIES)) {
                    const normalizedPath = path.toLowerCase();
                    if (servicePath.includes(normalizedPath)) {
                      serviceCategory = category;
                      break;
                    }
