  const [currentCategory, setCurrentCategory] = useState('');
  const [displayCategory, setDisplayCategory] = useState('');
  const [icons, setIcons] = useState([]);
  const [selectedIcons, setSelectedIcons] = useState([]);
  const [correctSelections, setCorrectSelections] = useState(0);
  const [timeElapsed, setTimeElapsed] = useState(0);
  const [score, setScore] = useState(0);
  const [gameStarted, setGameStarted] = useState(false);
  const [roundComplete, setRoundComplete] = useState(false);
  const [gameOver, setGameOver] = useState(false);
  const [showConfetti, setShowConfetti] = useState(false);
  const [playerName, setPlayerName] = useState('');
  const [showWelcome, setShowWelcome] = useState(true);
  const [highScores, setHighScores] = useState([
    { name: 'Eimis', score: 108 },
    { name: 'Eimis', score: 85 },
    { name: 'Eimis', score: 52 },
    { name: 'dd', score: 41 },
    { name: 'Eimis', score: 39 }
  ]);
  const TIME_LIMIT = 60; // 60 seconds time limit per round

  // Sound state
  const [soundsLoaded, setSoundsLoaded] = useState(false);
  const [isMuted, setIsMuted] = useState(false);
  const sounds = useRef({});

  // Game container ref
  const gameContainerRef = useRef(null);
  const timerRef = useRef(null);
  const categoryIconsRef = useRef([]);

  // Define sound files
  const soundFiles = {
    select: `${process.env.REACT_APP_AZURE_BUCKET_URL}/games/sounds/flip-switch.mp3`,
    correct: `${process.env.REACT_APP_AZURE_BUCKET_URL}/games/sounds/correct_sound.mp3`,
    incorrect: `${process.env.REACT_APP_AZURE_BUCKET_URL}/games/sounds/incorrect_sound.mp3`,
    hover: `${process.env.REACT_APP_AZURE_BUCKET_URL}/games/sounds/hover.mp3`,
    achievement: `${process.env.REACT_APP_AZURE_BUCKET_URL}/games/sounds/levelup.mp3`
  };

  // Helper function to check if a score is high enough to be in the high scores
  const checkHighScore = useCallback((newScore) => {
    if (highScores.length < 5 || newScore > highScores[highScores.length - 1].score) {
      // Add the new score
      const newHighScores = [...highScores, { name: playerName || 'Player', score: newScore }];

      // Sort and limit to top 5
      newHighScores.sort((a, b) => b.score - a.score);
      if (newHighScores.length > 5) {
        newHighScores.pop();
      }

      setHighScores(newHighScores);
      return true;
    }
    return false;
  }, [highScores, playerName]);

  // Helper function to safely create audio objects with timeout
  const createSafeAudio = useCallback((path) => {
    const audio = new Audio();
    return new Promise((resolve) => {
      audio.addEventListener('error', () => {
        console.warn(`Sound file not found: ${path}`);
        resolve(null);
      });
      audio.addEventListener('canplaythrough', () => {
        resolve(audio);
      });
      // Timeout fallback in case sound loading takes too long
      setTimeout(() => {
        if (audio.readyState < 3) {
          console.warn(`Sound loading timeout: ${path}`);
          resolve(null);
        }
      }, 3000);
      audio.src = path;
    });
  }, []);

  // Load all sounds
  useEffect(() => {
    const loadSounds = async () => {
      const soundPromises = Object.entries(soundFiles).map(async ([name, path]) => {
        try {
          const audio = await createSafeAudio(path);
          return [name, audio];
        } catch (error) {
          console.error(`Failed to load sound ${name}:`, error);
          return [name, null];
        }
      });

      try {
        const loadedSounds = await Promise.all(soundPromises);
        const soundsMap = Object.fromEntries(loadedSounds);
        sounds.current = soundsMap;

        // Check if at least some sounds loaded successfully
        const hasLoadedSounds = Object.values(soundsMap).some(sound => sound !== null);
        setSoundsLoaded(hasLoadedSounds);

        if (hasLoadedSounds) {
          console.log('🔊 Sounds loaded successfully');
        } else {
          console.warn('⚠️ No sounds could be loaded');
        }
      } catch (error) {
        console.error('Failed to load sounds:', error);
      }
    };

    loadSounds();
  }, [createSafeAudio]);

  // Toggle mute
  const toggleMute = useCallback(() => {
    setIsMuted(!isMuted);
  }, [isMuted]);

  // Play a sound effect
  const playSound = useCallback(
    (soundName) => {
      if (isMuted || !sounds.current[soundName]) return;
      const sound = sounds.current[soundName];
      sound.currentTime = 0;
      sound.play().catch(err => console.warn('Error playing sound:', err));
    },
    [isMuted]
  );

  // First, define the initGame function without any dependencies to other functions
  const initGame = useCallback(() => {
    const { category, displayName, icons: allIcons, targetIcons } = getRandomCategory();

    setCurrentCategory(category);
    setDisplayCategory(displayName);
    setIcons(allIcons);
    categoryIconsRef.current = targetIcons;
    setSelectedIcons([]);
    setCorrectSelections(0);
    setTimeElapsed(0);
    setRoundComplete(false);
    setGameOver(false);
    setGameStarted(true);

    // Clear any existing timer
    if (timerRef.current) {
      clearInterval(timerRef.current);
    }

    // Start timer
    const startTime = Date.now();
    timerRef.current = setInterval(() => {
      const newTime = Math.floor((Date.now() - startTime) / 1000);
      setTimeElapsed(newTime);

      // Check if time has run out
      if (newTime >= TIME_LIMIT) {
        // Handle timer expiry directly in the interval callback
        if (timerRef.current) {
          clearInterval(timerRef.current);
        }

        if (!roundComplete) {
          setGameOver(true);
          playSound('incorrect');
        }
      }
    }, 1000);

    console.log(`Starting new round with category: ${displayName}`);
    console.log(`Target icons:`, targetIcons);
  }, []);

  // Next, define nextCategory with only initGame dependency
  const nextCategory = useCallback(() => {
    // Stop any confetti
    setShowConfetti(false);

    // Reset game state for next round
    setGameOver(false);
    initGame();

    // Play sound effect
    playSound('select');
  }, [initGame, playSound]);

  // Then, define handleRoundComplete
  const handleRoundComplete = useCallback(() => {
    if (timerRef.current) {
      clearInterval(timerRef.current);
    }

    setRoundComplete(true);
    setShowConfetti(true);
    playSound('achievement');

    // Calculate score based on correct selections only
    const newScore = score + (categoryIconsRef.current.length * 5);
    setScore(newScore);

    // Check if it's a high score
    checkHighScore(newScore);
  }, [timeElapsed, playSound, score, checkHighScore]);

  // Use an effect for auto-advancing to next category
  useEffect(() => {
    let timer;

    if (roundComplete || gameOver) {
      timer = setTimeout(() => {
        nextCategory();
      }, 3000);
    }

    return () => {
      if (timer) clearTimeout(timer);
    };
  }, [roundComplete, gameOver, nextCategory]);

  // Handle card click
  const handleCardClick = (icon) => {
    playSound('select');

    if (selectedIcons.includes(icon)) {
      // If already selected, deselect it
      setSelectedIcons(prev => prev.filter(i => i !== icon));
      if (categoryIconsRef.current.includes(icon)) {
        setCorrectSelections(prev => prev - 1);
      }
    } else {
      // If not selected, select it
      setSelectedIcons(prev => [...prev, icon]);

      if (categoryIconsRef.current.includes(icon)) {
        // Correct selection
        const newCorrectSelections = correctSelections + 1;
        setCorrectSelections(newCorrectSelections);
        playSound('correct');

        // Check if all correct icons are selected
        if (newCorrectSelections === categoryIconsRef.current.length) {
          handleRoundComplete();
        }
      } else {
        // Incorrect selection
        playSound('incorrect');
        setScore(prev => Math.max(0, prev - 5)); // Penalty for wrong selection
      }
    }
  };

  // Handle player name input and start game
  const handleStartGame = () => {
    setShowWelcome(false);
    setGameStarted(true);
    initGame();
  };

  // Start the game when component mounts
  useEffect(() => {
    // Only auto-start if we're not showing the welcome screen
    if (!showWelcome) {
      initGame();
    }
    return () => {
      if (timerRef.current) {
        clearInterval(timerRef.current);
      }
    };
  }, [initGame, showWelcome]);

  // Format time for display
  const formatTime = (seconds) => {
    return `Time: ${seconds}s`;
  };

  // Welcome screen component
  const WelcomeScreen = () => {
    return (
      <div className="gameCanvas-welcome">
        <h1 className="gameCanvas-game-title">Azure Category Challenge</h1>

        <div className="gameCanvas-welcome-content">
          <h2>Welcome to the Category Challenge!</h2>
          <p>Select all icons that belong to the displayed category as quickly as possible.</p>

          <div className="gameCanvas-welcome-input">
            <label htmlFor="playerName">Enter your nickname:</label>
            <input 
              type="text" 
              id="playerName" 
              value={playerName} 
              onChange={(e) => setPlayerName(e.target.value)}
              placeholder="Your nickname"
            />
          </div>

          <button 
            className="gameCanvas-start-button"
            onClick={handleStartGame}
          >
            Start Game
          </button>
        </div>
      </div>
    );
  };

  // Player panel component
  const PlayerPanel = () => {
    return (
      <div className="gameCanvas-side-panel">
        <div className="player-info-box">
          <div className="player-name">
            PLAYER: Player: {playerName || 'Player'}
          </div>
        </div>

        <div className="score-container">
          <span role="img" aria-label="Spider">🕷️</span> Score: {score}
        </div>

        <div className="timer-container">
          TIME: Time: {timeElapsed}s
        </div>

        <div className="how-to-score-box">
          <div className="section-header">HOW TO SCORE:</div>
          <ul>
            <li>Correct selection: +5 points</li>
            <li>Wrong selection: -5 points</li>
            <li>Current progress: {correctSelections} out of {categoryIconsRef.current.length}</li>
          </ul>
        </div>

        <div className="high-scores-box">
          <div className="section-header">HIGH SCORES</div>
          {highScores.map((entry, index) => (
            <div key={index} className="high-score-entry">
              <span>{entry.name}:</span>
              <span>{entry.score}</span>
            </div>
          ))}
        </div>

        <div className="current-mission-box">
          <div className="section-header">CURRENT MISSION</div>
          <div className="mission-text">
            Find all {displayCategory} icons!
          </div>
        </div>
      </div>
    );
  };

  // Confetti component
  const Confetti = ({ active }) => {
    if (!active) return null;

    const confettiCount = 150;
    const confetti = [];

    for (let i = 0; i < confettiCount; i++) {
      const left = `${Math.random() * 100}%`;
      const animationDelay = `${Math.random() * 5}s`;
      const initialOpacity = Math.random();
      const size = `${Math.random() * 10 + 5}px`;
      const rotation = `${Math.random() * 360}deg`;
      const color = [
        '#00ffff', // Cyan (Azure color)
        '#0078d4', // Blue (Azure color)
        '#ffb900', // Gold
        '#e74c3c', // Red
        '#2ecc71', // Green
        '#9b59b6', // Purple
        '#3498db', // Light blue
      ][Math.floor(Math.random() * 7)];

      confetti.push(
        <div 
          key={i}
          className="memory-confetti-particle"
          style={{
            left,
            top: '-10px',
            width: size,
            height: size,
            backgroundColor: color,
            transform: `rotate(${rotation})`,
            opacity: initialOpacity,
            animationDelay,
          }}
        />
      );
    }

    return <div className="memory-confetti-container">{confetti}</div>;
  };

  // Main render function
  return (
    <div 
      className="gameCanvas-container" 
      ref={gameContainerRef}
    >
      {showWelcome ? (
        <WelcomeScreen />
      ) : (
        <React.Fragment>
          <Confetti active={showConfetti} />

          <h1 
            className="gameCanvas-game-title"
            style={{ '--azure-icon': `url(${process.env.PUBLIC_URL}/azure-icons/Microsoft_Azure_Cyan.svg)` }}
          >
            zure Category Challenge
          </h1>

          <div className="gameCanvas-game-with-panel">
            <div className="gameCanvas-canvas-wrapper" style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}>
              <div className="category-selection-header">
                <h2 className="category-selection-instruction">
                  Select all the cards which belong to the category: <span className="category-highlight">{displayCategory}</span>
                </h2>
              </div>

              <div className="gameCanvas-game-controls">
                <button 
                  className="memory-game-sound-toggle top-right"
                  onClick={toggleMute}
                  aria-label="Toggle sound"
                >
                  {isMuted ? '🔇' : '🔊'}
                </button>
              </div>

              <div className="category-selection-grid">
                {icons.map((icon, index) => (
                  <div
                    key={`${icon.name}-${index}`}
                    className={`category-selection-card ${selectedIcons.includes(icon.name) ? 
                      (categoryIconsRef.current.includes(icon.name) ? 'correct' : 'wrong') : ''}`}
                    onClick={() => handleCardClick(icon.name)}
                    onMouseEnter={() => playSound('hover')}
                  >
                    <img 
                      src={getServiceIconUrl(icon.path)} 
                      alt={icon.name} 
                      onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = `${process.env.PUBLIC_URL}/azure-icons/default.svg`;
                      }}
                    />
                    <div className="category-selection-card-text">{icon.name}</div>
                  </div>
                ))}
              </div>

              {roundComplete && (
                <div className="category-selection-success">
                  <h3>Great job! You found all {categoryIconsRef.current.length} icons in {timeElapsed} seconds.</h3>
                  <p>You earned {Math.max(100 - timeElapsed * 2, 10)} points!</p>
                </div>
              )}

              {/* Add a message about missed selections when the round is complete */}
              {gameOver && !roundComplete && (
                <div className="category-selection-failure-banner">
                  <h3>Time's up!</h3>
                  <p>You found {correctSelections} out of {categoryIconsRef.current.length} icons.</p>
                  <p>Looking for: {categoryIconsRef.current.join(', ')}</p>
                </div>
              )}
            </div>

            <PlayerPanel />
          </div>
        </React.Fragment>
      )}
    </div>
  );
};

export default IconsSelectCategoryFast;
