import React, { useState, useEffect } from 'react';
import ReactFlow, { 
  Background, 
  Controls, 
  MiniMap,
  useNodesState,
  useEdgesState,
  MarkerType,
  Handle
} from 'reactflow';
import 'reactflow/dist/style.css';
import { motion } from 'framer-motion';
import { getCachedServiceIcon } from '../services/IconResolver';

const AWSServiceNode = ({ data }) => (
  <motion.div
    className={`aws-service-node ${data.isMissing ? 'missing' : ''}`}
    initial={{ scale: 0, opacity: 0 }}
    animate={{ scale: 1, opacity: 1 }}
    exit={{ scale: 0, opacity: 0 }}
    whileHover={{ scale: 1.1 }}
    transition={{ type: "spring", stiffness: 300 }}
  >
    <Handle 
      type="target" 
      position="top" 
      id={`${data.id}-target`}
      className="handle"
    />
    <div className="node-content">
      {data.isMissing ? (
        <div className="missing-service-placeholder">
          <span>?</span>
        </div>
      ) : (
        <img src={data.icon} alt={data.label} className="service-icon" />
      )}
      <span className="service-label">{data.label}</span>
    </div>
    <Handle 
      type="source" 
      position="bottom" 
      id={`${data.id}-source`}
      className="handle"
    />
  </motion.div>
);

const nodeTypes = {
  awsService: AWSServiceNode
};

const createNodeId = (serviceName) => {
  if (typeof serviceName !== 'string') {
    console.error('Invalid service name:', serviceName);
    return 'invalid-service';
  }
  return serviceName.toLowerCase().replace(/\s+/g, '-');
};

const AWSArchitectureDisplay = ({ architecture, missingServices }) => {
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);

  // Add a ref to track if we've logged the warning
  const hasLoggedWarning = React.useRef(false);

  React.useEffect(() => {
    // Only log once per mount
    if (!architecture?.services && !hasLoggedWarning.current) {
      hasLoggedWarning.current = true;
      console.log('âš ï¸ Waiting for architecture data...');
      return;
    }

    if (architecture?.services) {
      console.log('ðŸ“Š Rendering architecture:', {
        servicesCount: architecture.services.length,
        connectionsCount: architecture.connections?.length
      });

      const newNodes = [];
      const newEdges = [];
      const nodePositions = {};

      // Calculate total width needed
      const HORIZONTAL_SPACING = 250; // Increased spacing between nodes
      const startX = 50; // Starting position
      const centerY = 150; // Fixed Y position for better visibility

      // First, create all nodes with more spacing
      architecture.services.forEach((service, index) => {
        const nodeId = service.id || service.name.toLowerCase().replace(/[^a-z0-9]+/g, '-');
        const node = {
          id: nodeId,
          type: 'awsService',
          // Position nodes with more space between them
          position: { 
            x: startX + (index * HORIZONTAL_SPACING), 
            y: centerY + (index % 2 === 0 ? -20 : 20) // Slight vertical offset for better edge visibility
          },
          data: {
            label: service.name,
            icon: service.icon,
            isMissing: service.name.toLowerCase().includes('missing')
          },
          style: {
            width: 120, // Fixed width for consistency
            height: 80  // Fixed height for consistency
          }
        };
        newNodes.push(node);
        nodePositions[nodeId] = node.position;
      });

      // Create edges with improved styling
      if (architecture.connections) {
        architecture.connections.forEach((conn) => {
          const fromId = typeof conn.from === 'string' ? 
            conn.from.toLowerCase().replace(/[^a-z0-9]+/g, '-') : 
            conn.from.id;
          const toId = typeof conn.to === 'string' ? 
            conn.to.toLowerCase().replace(/[^a-z0-9]+/g, '-') : 
            conn.to.id;

          if (nodePositions[fromId] && nodePositions[toId]) {
            newEdges.push({
              id: `${fromId}-${toId}`,
              source: fromId,
              target: toId,
              type: 'smoothstep',
              animated: true,
              style: { 
                stroke: '#4FD1C5', 
                strokeWidth: 2,
                opacity: 0.8
              },
              markerEnd: { 
                type: MarkerType.ArrowClosed,
                width: 20,
                height: 20
              },
              // Add curve to the connections for better visibility
              sourceHandle: 'right',
              targetHandle: 'left',
              // Adjust the curve of the connection
              curvature: 0.4
            });
          }
        });
      }

      setNodes(newNodes);
      setEdges(newEdges);
    }

    // Cleanup function
    return () => {
      hasLoggedWarning.current = false;
    };
  }, [architecture, setNodes, setEdges]);

  // Early return if no data
  if (!architecture?.services) {
    return (
      <div className="architecture-visualizer-loading">
        <span>Loading architecture...</span>
      </div>
    );
  }

  return (
    <div style={{ height: '500px', width: '100%' }}> {/* Increased height for better visibility */}
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        nodeTypes={nodeTypes}
        fitView
        fitViewOptions={{
          padding: 0.2, // Add padding around the view
          minZoom: 0.5,
          maxZoom: 1.5
        }}
        defaultViewport={{ zoom: 1 }}
        minZoom={0.5}
        maxZoom={1.5}
      >
        <Background color="#4FD1C5" gap={16} size={1} />
        <Controls />
        <MiniMap 
          nodeColor={(node) => node.data.isMissing ? '#FF5656' : '#4FD1C5'}
          maskColor="rgba(13, 25, 35, 0.7)"
        />
      </ReactFlow>
    </div>
  );
};

export default AWSArchitectureDisplay;
