import React from 'react';
import { motion } from 'framer-motion';
import AWS_ICONS from '../config/aws-icons';

const AWSArchitectureDisplay = ({ architecture, missingServices, onServiceClick }) => {
  const getServiceIcon = (serviceId) => {
    return AWS_ICONS[serviceId] || '/aws-icons/default.png';
  };

  if (!architecture || !architecture.services) {
    return (
      <div className="architecture-container">
        <motion.div 
          className="architecture-diagram"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          <div className="loading-state">
            Loading Architecture...
          </div>
        </motion.div>
      </div>
    );
  }

  return (
    <div className="architecture-container">
      <motion.div 
        className="architecture-diagram"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.5 }}
      >
        {architecture.services.map((service, index) => {
          console.log('Loading icon for service:', service.id, getServiceIcon(service.id));
          return (
            <div 
              key={service.id}
              className={`service-node ${missingServices?.includes(service.id) ? 'missing' : ''}`}
              onClick={() => missingServices?.includes(service.id) && onServiceClick(service.id)}
            >
              <img 
                src={getServiceIcon(service.id)}
                alt={service.name}
                onError={(e) => {
                  console.error(`Failed to load icon for ${service.id}`);
                  e.target.src = '/aws-icons/default.png';
                }}
              />
              <span>{service.name}</span>
            </div>
          );
        })}
      </motion.div>
    </div>
  );
};

export default AWSArchitectureDisplay;
