const AWSArchitectureDisplay = ({ architecture }) => {
  if (!architecture || !architecture.services) {
    return <div className="architecture-container">No architecture to display</div>;
  }

  const calculateServicePositions = () => {
    const positions = {};
    const totalWidth = 1000;
    const totalHeight = 400;
    const serviceWidth = 120;
    const serviceHeight = 100;

    const serviceCount = architecture.services.length;
    const horizontalGap = (totalWidth - (serviceCount * serviceWidth)) / (serviceCount + 1);

    architecture.services.forEach((service, index) => {
      positions[service.name] = {
        x: horizontalGap + (index * (serviceWidth + horizontalGap)),
        y: totalHeight / 2 - serviceHeight / 2
      };
    });

    return positions;
  };

  const positions = calculateServicePositions();

  return (
    <div className="architecture-container">
      <div className="architecture-diagram">
        <svg className="connections-layer" width="100%" height="100%">
          {architecture.connections.map(({ from, to }, index) => {
            const fromPos = positions[from];
            const toPos = positions[to];
            if (fromPos && toPos) {
              return (
                <line
                  key={`${from}-${to}-${index}`}
                  x1={fromPos.x + 60}
                  y1={fromPos.y + 50}
                  x2={toPos.x + 60}
                  y2={toPos.y + 50}
                  stroke="#4FD1C5"
                  strokeWidth="2"
                />
              );
            }
            return null;
          })}
        </svg>

        <div className="services-layer">
          {architecture.services.map(service => {
            const position = positions[service.name];
            return (
              <div
                key={service.name}
                className="service-node"
                style={{
                  transform: `translate(${position.x}px, ${position.y}px)`
                }}
              >
                <div className="service-icon">
                  <img
                    src={`/aws-icons/${service.name.toLowerCase().replace(/\s+/g, '-')}.png`}
                    alt={service.name}
                    onError={(e) => {
                      e.target.src = '/aws-icons/default.png';
                    }}
                  />
                </div>
                <div className="service-name">{service.name}</div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default AWSArchitectureDisplay;
