import React from 'react';
import { useDrag } from 'react-dnd';

const ServiceOption = ({ service, onSelect }) => {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: 'AWS_SERVICE',
    item: { id: service.id },
    collect: (monitor) => ({
      isDragging: monitor.isDragging()
    })
  }));

  return (
    <div
      ref={drag}
      className={`service-option ${isDragging ? 'dragging' : ''}`}
      onClick={() => onSelect(service.id)}
      style={{ opacity: isDragging ? 0.5 : 1 }}
    >
      <img src={service.icon} alt={service.name} className="service-icon" />
      <span className="service-name">{service.name}</span>
    </div>
  );
};

const ServiceOptionsPanel = ({ options, onSelect, isLoading }) => {
  if (isLoading) {
    return <div className="options-container loading">Loading options...</div>;
  }

  return (
    <div className="options-container">
      {options.map((service) => (
        <ServiceOption 
          key={service.id}
          service={service}
          onSelect={onSelect}
        />
      ))}
    </div>
  );
};

export default ServiceOptionsPanel;
