import React, { useState, useEffect } from 'react';
import '../styles/NicknameInput.css';

const NicknameInput = ({ onSaveNickname, savedNickname }) => {
  const [nickname, setNickname] = useState('');

  useEffect(() => {
    // Load saved nickname from localStorage if available
    const savedName = localStorage.getItem('playerNickname') || savedNickname || '';
    setNickname(savedName);
  }, [savedNickname]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (nickname.trim()) {
      // Save to localStorage for future use
      localStorage.setItem('playerNickname', nickname);
      onSaveNickname(nickname);
    }
  };

  return (
    <div className="nickname-input-container">
      <form onSubmit={handleSubmit}>
        <div className="input-group">
          <label htmlFor="nickname">Nickname:</label>
          <input
            type="text"
            id="nickname"
            value={nickname}
            onChange={(e) => setNickname(e.target.value)}
            placeholder="Enter your nickname"
            maxLength={20}
            required
          />
        </div>
        <button type="submit" className="save-nickname-btn">Save</button>
      </form>
    </div>
  );
};

export default NicknameInput;
