import ReactDOM from 'react-dom';
import { useEffects } from '../context/EffectsContext';
import ReactConfetti from 'react-confetti';
import { useWindowSize } from 'react-use';

function Effects() {
  const { width, height } = useWindowSize();
  const { showConfetti, showFireworks } = useEffects();

  return ReactDOM.createPortal(
    <>
      {showConfetti && (
        <ReactConfetti
          width={width}
          height={height}
          recycle={false}
          numberOfPieces={200}
          style={{ 
            position: 'fixed',
            top: 0,
            left: 0,
            pointerEvents: 'none',
            zIndex: 9999
          }} 
        />
      )}
      {showFireworks && (
        <ReactConfetti
          width={width}
          height={height}
          recycle={true}
          numberOfPieces={500}
          colors={['#FFD700', '#FF0000', '#00FF00', '#0000FF', '#FF00FF']}
          style={{ 
            position: 'fixed',
            top: 0,
            left: 0,
            pointerEvents: 'none',
            zIndex: 9999
          }}
        />
      )}
    </>,
    document.body
  );
}

export default Effects;
