import React, { useState, useEffect, useCallback } from 'react';
import { DndContext, useSensor, useSensors, PointerSensor, TouchSensor } from '@dnd-kit/core';
import ArchitectureVisualizer from './ArchitectureVisualizer';
import ServiceOptionsPanel from './ServiceOptionsPanel';
import { generateArchitecture } from '../services/AWSArchitectureAI';
import AWSArchitectureDisplay from './AWSArchitectureDisplay';
import DraggableService from './DraggableService';



const AWSArchitectureGame = () => {
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [architecture, setArchitecture] = useState(null);
  const [architectureInfo, setArchitectureInfo] = useState(null);
  const [currentQuestion, setCurrentQuestion] = useState(null);
  const [serviceOptions, setServiceOptions] = useState([]);
  const [difficulty] = useState('BEGINNER');

  const API_URL = 'https://p7bwtrx47e.execute-api.us-east-1.amazonaws.com/stage/amazon-architecture';

  const sensors = useSensors(
    useSensor(PointerSensor, {
      activationConstraint: {
        distance: 8,
      },
    }),
    useSensor(TouchSensor)
  );

  const loadNextArchitecture = useCallback(async () => {
    if (isLoading) return; // Prevent concurrent calls

    setIsLoading(true);
    setError(null);

    try {
      console.log('Sending request to:', API_URL);

      const response = await fetch(API_URL, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
          'Origin': 'http://localhost:3000'
        },
        mode: 'cors',
        credentials: 'omit',
        body: JSON.stringify({ difficulty })
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      console.log('Raw API Response:', data);

      // The response is the data itself, no need to check for body property
      if (!data || !data.architecture || !data.services || !data.connections || !data.missingServices) {
        throw new Error('Missing required fields in API response');
      }

      setArchitectureInfo({
        name: data.architecture.name,
        description: data.architecture.description
      });

      setArchitecture({
        services: data.services,
        connections: data.connections
      });

      if (data.missingServices && data.missingServices.length > 0) {
        setCurrentQuestion(data.missingServices[0]);
        setServiceOptions(data.missingServices[0].options);
      } else {
        throw new Error('No missing services found in response');
      }

    } catch (error) {
      console.error('Failed to load architecture:', error);
      setError(`Failed to load architecture: ${error.message}`);
    } finally {
      setIsLoading(false);
    }
  }, [difficulty, isLoading]); // Add dependencies

  useEffect(() => {
    if (!architecture && !isLoading) {
      loadNextArchitecture();
    }
  }, [architecture, isLoading, loadNextArchitecture]);

  const handleServiceSelection = async (selectedServiceId) => {
    const selectedOption = currentQuestion.options.find(opt => opt.id === selectedServiceId);

    if (selectedOption?.isCorrect) {
      // Add points based on whether it was the optimal choice
      const points = selectedOption.isOptimal ? 2 : 1;
      setScore(prev => prev + points);

      // Find next question or load new architecture
      const currentQuestionIndex = data.questions.findIndex(q => q.position === currentQuestion.position);
      if (currentQuestionIndex < data.questions.length - 1) {
        const nextQuestion = data.questions[currentQuestionIndex + 1];
        setCurrentQuestion(nextQuestion);
        setServiceOptions(nextQuestion.options);
      } else {
        await loadNextArchitecture();
      }
    }
  };

  const handleDragEnd = (event) => {
    const { active, over } = event;

    if (over && over.id === 'architecture-drop-zone') {
      handleServiceSelection(active.id);
    }
  };

  if (isLoading) {
    return <div className="loading">Loading architecture...</div>;
  }

  if (error) {
    return (
      <div className="error-container">
        <p className="error-message">{error}</p>
        <button onClick={loadNextArchitecture}>Try Again</button>
      </div>
    );
  }

  return (
    <div className="game-container">
      <div className="game-header">
        <h2>{architectureInfo?.name}</h2>
        <div className="score">Score: {score}</div>
      </div>

      <div className="architecture-description">
        {architectureInfo?.description}
      </div>

      <div className="question-container">
        <h3 className="question-text">{currentQuestion?.text}</h3>
        <p className="optimization-focus">
          Focus: {currentQuestion?.optimizationFocus}
        </p>
      </div>

      <DndContext sensors={sensors} onDragEnd={handleDragEnd}>
        <div className="architecture-section">
          <AWSArchitectureDisplay 
            architecture={architecture}
            missingServices={[currentQuestion?.position]}
          />
        </div>

        <div className="service-options">
          {serviceOptions.map((option, index) => (
            <DraggableService
              key={`${option.service}-${index}`}
              service={{
                id: option.service,
                name: option.service,
                icon: `/aws-icons/${option.service.toLowerCase().replace(/\s+/g, '-')}.png`,
                rating: option.rating,
                explanation: option.explanation
              }}
              onClick={() => handleServiceSelection(option.service)}
            />
          ))}
        </div>
      </DndContext>
    </div>
  );
};

export default AWSArchitectureGame;
