const AWS_ICON_CATEGORIES = {
  ANALYTICS: 'Arch_Analytics',
  APP_INTEGRATION: 'Arch_App-Integration',
  ARTIFICIAL_INTELLIGENCE: 'Arch_Artificial-Intelligence',
  BLOCKCHAIN: 'Arch_Blockchain',
  BUSINESS_APPLICATIONS: 'Arch_Business-Applications',
  COMPUTE: 'Arch_Compute',
  CONTAINERS: 'Arch_Containers',
  DATABASE: 'Arch_Database',
  DEVELOPER_TOOLS: 'Arch_Developer-Tools',
  MANAGEMENT: 'Arch_Management-Governance',
  NETWORKING: 'Arch_Networking-Content-Delivery',
  SECURITY: 'Arch_Security-Identity-Compliance',
  STORAGE: 'Arch_Storage'
};

const SERVICE_MAPPINGS = {
  "Amazon Route 53": "Arch_Networking-Content-Delivery/16/Arch_Amazon-Route-53_16.svg",
  "Amazon S3": "Arch_Storage/16/Arch_Amazon-Simple-Storage-Service_16.svg",
  "Amazon CloudFront": "Arch_Networking-Content-Delivery/16/Arch_Amazon-CloudFront_16.svg",
  "AWS Certificate Manager": "Arch_Security-Identity-Compliance/16/Arch_AWS-Certificate-Manager_16.svg",
  "Amazon RDS": "Arch_Database/16/Arch_Amazon-RDS_16.svg",
  "Amazon EC2": "Arch_Compute/16/Arch_Amazon-EC2_16.svg",
  "Amazon DynamoDB": "Arch_Database/16/Arch_Amazon-DynamoDB_16.svg",
  "AWS Lambda": "Arch_Compute/16/Arch_AWS-Lambda_16.svg",
  "Amazon VPC": "Arch_Networking-Content-Delivery/16/Arch_Amazon-Virtual-Private-Cloud_16.svg",
  "Amazon ECS": "Arch_Containers/16/Arch_Amazon-Elastic-Container-Service_16.svg",
  "Amazon ElastiCache": "Arch_Database/16/Arch_Amazon-ElastiCache_16.svg",
  "Amazon CloudWatch": "Arch_Management-Governance/16/Arch_Amazon-CloudWatch_16.svg",
  "AWS Elastic Beanstalk": "Arch_Compute/16/Arch_AWS-Elastic-Beanstalk_16.svg"
};

const S3_BUCKET_URL = 'https://hackthon-backend-files-ep-2024.s3.amazonaws.com';
const ICONS_BASE_PATH = '/amazon-icons-set/Architecture-Service-Icons_06072024';

// Cache for resolved icons
const iconCache = new Map();

const getServiceIcon = (serviceName) => {
  if (!serviceName) {
    console.error('âŒ No service name provided to getServiceIcon');
    return null;
  }

  // Find the mapping for this service
  const iconPath = SERVICE_MAPPINGS[serviceName];
  if (!iconPath) {
    console.error(`âŒ No icon mapping found for service: ${serviceName}`);
    return null;
  }

  // Construct the full URL
  const fullUrl = `${S3_BUCKET_URL}${ICONS_BASE_PATH}/${iconPath}`;
  console.log(`ðŸ”— Constructed icon URL for ${serviceName}:`, fullUrl);

  return fullUrl;
};

const getCachedServiceIcon = (serviceName) => {
  if (iconCache.has(serviceName)) {
    return iconCache.get(serviceName);
  }

  const iconUrl = getServiceIcon(serviceName);
  if (iconUrl) {
    iconCache.set(serviceName, iconUrl);
  }

  return iconUrl;
};

// Single export statement for all needed functions and constants
export {
  getCachedServiceIcon,
  AWS_ICON_CATEGORIES,
  SERVICE_MAPPINGS
};
