const AWSArchitectureDisplay = ({ architecture }) => {
  if (!architecture || !architecture.services) {
    return <div className="architecture-container">No architecture to display</div>;
  }

  const calculateServicePositions = () => {
    const positions = {};
    const levels = {};

    architecture.connections.forEach(({ from, to }) => {
      if (!levels[from]) levels[from] = 0;
      levels[to] = (levels[from] || 0) + 1;
    });

    const levelWidth = 200;
    const serviceHeight = 100;

    architecture.services.forEach(service => {
      const level = levels[service.name] || 0;
      const servicesAtLevel = Object.values(levels).filter(l => l === level).length;
      const index = Object.keys(levels).filter(s => levels[s] === level).indexOf(service.name);

      positions[service.name] = {
        x: level * levelWidth + 100,
        y: (index + 1) * serviceHeight
      };
    });

    return positions;
  };

  const positions = calculateServicePositions();

  return (
    <div className="architecture-display">
      <svg className="connections" width="100%" height="100%">
        {architecture.connections.map(({ from, to }, index) => {
          const fromPos = positions[from];
          const toPos = positions[to];
          if (fromPos && toPos) {
            return (
              <line
                key={`${from}-${to}-${index}`}
                x1={fromPos.x + 50}
                y1={fromPos.y + 25}
                x2={toPos.x + 50}
                y2={toPos.y + 25}
                stroke="#4FD1C5"
                strokeWidth="2"
              />
            );
          }
          return null;
        })}
      </svg>

      {architecture.services.map(service => {
        const position = positions[service.name];
        return (
          <div
            key={service.id}
            className="service-node"
            style={{
              position: 'absolute',
              left: `${position.x}px`,
              top: `${position.y}px`,
            }}
          >
            <div className="service-icon">
              <img
                src={service.icon}
                alt={service.name}
                onError={(e) => {
                  e.target.src = '/aws-icons/default.png';
                }}
              />
            </div>
            <div className="service-name">{service.name}</div>
          </div>
        );
      })}
    </div>
  );
};

export default AWSArchitectureDisplay;
